INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("50414", "a refreshing salad to usher in summer", "tried this great salad out from a cookbook i own. and i thought, hey i should share this great recipe with my 'zaar family. so here it is. summer is coming for heaps of you...i think it will come in handy.", "10", "7", "['remove skin , seed and the white part from the watermelon', 'cut into bite size pieces', 'place watermelon in a serving bowl', 'drizzle lime juice over and season with a little salt and freshly ground black pepper', 'add feta , mint and lime zest', 'garnish with whole sprigs of mint', 'a rich blue-veined cheese']", "5", "174.5", "16.0", "40.0", "23.0", "15.0", "37.0", "4.0", "None", "lunch", "5-ingredients-or-less,beginner-cook,easy,no-cook", "salads", "None", "dinner-party,valentines-day", "cheese,fruit,vegetables", "summer", "19.5");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("448592", "spice kissed masoor dal split red lentils", "i love tasty food, especially when it can be made quickly and be made from ingredients i usually have on hand. i've cooked many recipes for dal over the years, eaten many varieties of dal dishes out at restaurants, and i'm always looking for the next best dal recipe that i can make at home. this is it!", "35", "23", "['in a medium sized pot over medium flame , add 1 tbsp peanut oil , 1 tsp garlic powder , 1 tsp ginger powder , and 1 tsp garam masala powder', 'stir spices and oil together , allow to fry slightly', 'add the dal and water to cover , turn the heat to high , and bring to a boil', 'skim off foam that forms on top and discard', 'cover and turn the heat to low', 'allow to simmer until dal begins to fall apart and is cooked through', 'while cooking , monitor the water level', 'if it is too low , the dal will burn to the bottom of the pan , so add more water', 'if the water level is too high , uncover the pan so some will evaporate', 'when done , the dal should be a porridge consistency--not watery , but not burning to the bottom of the pot', 'heat frozen green peas , drain excess water , and add to cooked dal', 'taste dal and adjust salt', 'since you have not salted the pan yet , you may need to add a few teaspoons to tablespoons of salt', 'add slowly--you can always add more but it is hard to get rid of too much', 'make the tadka: in a frying pan over medium heat add the butter , 1 tsp tumeric powder , 2 tsp garam masala powder , and 1 / 2 tsp red pepper flakes', 'fry lightly until spices release their fragrance', 'add the onion and fry for another few minutes to the softness you desire', 'add the garlic and ginger and fry for another 2 minutes', 'add tadka mixture to dal mixture and stir to combine', 'taste and adjust seasonings as necessary', 'serve spice kissed masoor dal over rice with yogurt , raita , red raw onions , cucumber , lettuce , tomatoes or other fresh vegetables of your liking', 'to read about dal , visit the wikipedia page at:', 'to read about tadka , visit the wikipedia page at:']", "13", "372.0", "14.0", "10.0", "2.0", "42.0", "18.0", "17.0", "None", "lunch", "easy", "beans", "None", "dinner-party", "beans", "None", "28.62");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("50411", "leftover roast beef spicy salad", "had way too much of roast beef one time, and whipped this salad up to make up for the amount of meat i had consumed, and the lack of vegetables in a diet. it worked so well, i have started making extra roast beef, just so that i could have heaps of leftovers for this wonderful salad.", "10", "6", "['wash and dry mint leaves and watercress in a salad spinner', 'toss the packaged salad leaves and watercress in a salad bowl and top with tomatoes and onion', 'add beef slices , torn into bite-sized pieces', 'mix dressing in a bowl , stir well to combine', 'cover salad with spicy dressing', 'top with mint leaves and serve with rotti for a twist']", "12", "95.9", "4.0", "17.0", "15.0", "21.0", "5.0", "2.0", "None", "lunch", "beginner-cook,easy,no-cook,one-dish-meal", "salads", "None", "dinner-party,new-years,picnic,st-patricks-day,valentines-day", "beef,vegetables", "fall,spring,summer,winter", "39.77");
