INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("346354", "ww caprese rice salad", "ww used jasmine rice, which gives a slightly 'flowery' flavour to this italian-inspired dish. but it can be made with other exotic varieties of rice as well, such as basmati. 4 pts per serving, from weight watchers.", "40", "8", "['in a large serving bowl , mix together 2 tablespoons olive oil with the vinegar , basil , tomatoes , mozzarella , salt and pepper', 'set aside while rice cooks', 'heat remaining teaspoon of olive oil in a medium saucepan over medium heat', 'add onion and cook until softened , about 3 minutes', 'add rice and pint water', 'bring to the boil , cover and simmer until water is absorbed and rice is tender , about 15 - 20 minutes', 'toss rice with tomato mixture', 'serve warm or at room temperature']", "9", "216.2", "13.0", "8.0", "5.0", "14.0", "17.0", "8.0", "None", "lunch", "3-steps-or-less,easy,kid-friendly", "salads", "None", "picnic", "rice", "None", "29.95");
