INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("11648", "greek pita lunch", "if you do not want to serve with the tahini dressing just serve with your favorite dressing. try the tahini dressing on other salads, it is excellent. this is an easy to make lunch for anyone not just vegetarians", "25", "9", "['in a bowl combine', 'cottage cheese with feta , basil , oregano , garlic , tahini , pepper to taste', 'mix well', 'put 1 / 2 cup of the cheese mixture in each half pita pocket', 'put in 400f degrees oven for 5-7 minutes', 'add spinach , tomatoes , onion rings , pepper and sprouts to each pocket', 'serve with tahini dressing on the side', '--------tahinidressing-----------', 'mix tahini with lemon juice , then slowly add water until the tahini , thins out stir in the cumin , parsley , tamari and cayenne']", "19", "732.4", "68.0", "41.0", "161.0", "79.0", "60.0", "17.0", "None", "lunch", "None", "None", "oven", "None", "cheese,vegetables", "None", "24.69");
