INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("413290", "orecchiette pasta with veal capers and herb white wine sauce", "i made this for my 'foody' italian spouse and it totally impressed! this ended valentine's day on a very high note (*wink*). packed full of earthy flavors and light, creamy sauce. this will now be part of our bi-weekly recipe rotation!", "35", "11", "['in a large skillet , heat the olive oil', 'add onion and garlic and cook over moderately high heat , stirring frequently , until softened , about 5 minutes', 'add the ground veal , season with salt and pepper , and cook until veal is no longer pink and most of the liquid has evaporated , about 8 minutes', 'add the wine to the skillet and boil over high heat until nearly evaporated , about 5 minutes', 'reduce heat to moderate , and add the chicken stock , thyme , rosemary and capers and simmer until the liquid is reduced by half , about 10 minutes', 'meanwhile , cook the orecchiette in large pot of boiling , heavily salted water until al dente', 'drain pasta well , then return it to the pot', 'on top of the pasta , add the cheese , parsley , and butter , and stir gently', 'now add the veal sauce mixture to the pasta , stir gently', 'cook the pasta and sauce over moderate heat , stirring frequently , until sauce is thick and creamy , about 2 minutes', 'transfer to bowls and serve right away']", "15", "707.4", "39.0", "17.0", "72.0", "79.0", "50.0", "24.0", "None", "lunch", "None", "None", "None", "None", "None", "None", "34.62");
