INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("239001", "fried green tomato and bacon sandwich", "people beg me to make these sandwiches. it's a great way to use extra tomatoes from your garden. some grocery stores carry green tomatoes year round. this is my very favorite sandwich ever. the fresh mozzarella cheese can be substituted with regular mozzarella but it won't be a yummy! you can also use tempeh bacon for a vegetarian version (not vegan).", "20", "9", "['mix cornmeal , salt and pepper', 'coat green tomato slices with cornmeal mix', 'heat oil in frying pan', 'fry green tomatoes until lightly browned and slightly soft', 'coat bread slices with chipotle mayonnaise', 'stack with bacon , cheese , fried green tomatoes and red tomato', 'melt butter in frying pan', 'grill at medium-low temperature until cheese is melted', 'eat hot or cold']", "11", "1224.1", "129.0", "28.0", "71.0", "55.0", "99.0", "30.0", "None", "lunch", "None", "sandwiches", "None", "None", "None", "None", "33.84");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("185151", "japanese hambagu hamburg steak", "this is a popular japanese dish, sort of a really tasty take on salisbury steak. its quick, cheap, easy and sooo good. panko bread crumbs are key here..", "25", "17", "['chop onion finely', 'heat vegetable oil in a frying pan and saute onion well and set aside', 'put egg and milk in a bowl and mix well', 'add breadcrumb in the bowl and mix lightly and set aside', 'put beef in another bowl and add cooked onion and breadcrumb mixture in it', 'season the beef mixture with salt and pepper', 'mix the meat well with hands well', 'make four meat balls and toss each ball from one hand to the other hand repeatedly', 'make four hamburger patties and indent the center of each one with a finger', 'heat oil in a pan and saute hamburgers over medium heat', 'turn over and saute the other side', 'add 1 / 4 cup of hot water in the pan and put lid and steam hamburgers for a few minutes', 'remove the lid and cook until the water is gone', 'transfer hamburgers into individual plates', 'add ketchup and worcestershire sauce in the pan after hamburgers are removed', 'mix quickly over low heat to make sauce', 'pour the sauce over hamburgers']", "9", "404.4", "31.0", "24.0", "22.0", "54.0", "38.0", "8.0", "None", "lunch", "None", "None", "None", "None", "None", "None", "30.0");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("503392", "grilled chicken tzatziki wrap", "this delicious wrap is a slightly modified version from styleathome.com. for the best flavor it requires a marinade time of 2 hours minimum, which is not included in the prep time.", "35", "15", "['to prepare chicken marinade , whisk all marinade ingredients except chicken breasts together in a bowl', 'combine marinade with chicken breasts in a large zip-lock bag , seal and gently shake to evenly distribute marinade over chicken', 'place in refrigerator and chill for 2 to 4 hours', 'meanwhile , prepare the cucumber relish', 'lay cucumber slices in a sieve placed over a bowl and sprinkle with pinch of salt', 'leave for about 30 to 40 minutes , or until cucumbers soften and their liquid begins to drain into the bowl', 'stir occasionally', 'combine the tomato , onion , olives , parsley , dill , garlic , olive oil and lemon juice in a medium bowl', 'stir in drained cucumbers , then cover the bowl and refrigerate for 1 to 2 hours', 'grease grill and preheat to medium-high', 'remove chicken from zip-lock bag and discard marinade', 'grill chicken breasts until cooked through , about 12 minutes', 'remove chicken from grill , let rest for a few minutes and slice thinly on the diagonal', 'lay chicken slices on top of each pita round and divide relish evenly among them', 'garnish with a little extra yogurt and serve']", "19", "528.6", "31.0", "60.0", "50.0", "67.0", "19.0", "17.0", "grilling", "lunch", "None", "None", "None", "dinner-party", "chicken", "None", "31.69");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("78576", "chili cheddar pinwheels", "these are great for parties, holidays or for a late night snack. i love the crescent dough for a change from the tortillas. one thing i know is, they will disappear in no time!", "37", "11", "['in a mixing bowl , beat cream cheese', 'add the cheddar cheese , chilies , picante sauce , chili powder , garlic salt and onion powder', 'separate each tube of crescent roll dough into four rectangles', 'press perforations to seal', 'spread about 1 / 4 cup cheese mixture over each rectangle', 'roll up jelly-roll style , starting with a short side', 'wrap in plastic wrap and chill for at least 1 hour', 'cut each roll into eight slices', 'place on ungreased baking sheets', 'sprinkle with additional chili powder , if desired', 'bake at 350 degrees for 10-12 minutes or until golden brown']", "8", "42.2", "3.0", "1.0", "2.0", "2.0", "6.0", "1.0", "None", "lunch", "easy,kid-friendly", "None", "oven,refrigerator", "christmas,dinner-party,easter,new-years,thanksgiving", "cheese", "None", "30.98");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("107853", "chicken lo mein soft noodles", "wonderful flavor and easy to make. a good way to use leftover chicken.", "50", "8", "['cook the noodles in salted boiling water , rinse in cold water , and drain', 'in a wok or large skillet , saute the chicken in hot oil for 2 minutes', 'add the vegetables and saute for another 2-3 minutes', 'add the chicken stock and mix well', 'cover , reduce heat and simmer for 10 minutes', 'mix together the cornstarch , water , soy sauce and teriyaki sauce until smooth , and add the mixture to the chicken and vegetables', 'season with salt and pepper', 'stir in the noodles , re-heat and serve immediately']", "15", "311.3", "17.0", "21.0", "48.0", "28.0", "9.0", "12.0", "stir-fry", "lunch", "beginner-cook,easy,kid-friendly,one-dish-meal", "None", "stove-top", "None", "chicken,vegetables", "fall,summer", "12.54");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("461206", "salt grilled chicken thighs", "from just bento.com this is one of my favorite things to take for lunch! it's so simple, yet the salt brings out the wonderful flavor of the chicken, and crisping the skin in the pan makes it taste even better. i like to salt the chicken the night before, cover, and refrigerate overnight; then cook in the morning, although you could do the whole thing the night before.", "75", "8", "['butterfly the chicken , to get an even thickness and ensure fast cooking', 'sprinkle both sides liberally with salt', 'let stand , chilled , for 1 hour , then pat off any excess moisture with a paper towel', 'heat a non stick skillet over medium heat', 'add chicken , skin side down', 'cook until skin is golden brown', 'turn over , and cook until chicken is cooked through', 'remove from pan and let rest for a few minutes , then slice into bite sized pieces']", "3", "198.3", "22.0", "0.0", "2.0", "32.0", "20.0", "0.0", "None", "lunch", "5-ingredients-or-less,easy", "None", "None", "None", "chicken", "None", "23.03");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("519074", "classic manapua steamed buns hawaiian style", "manapua or baozi or steamed bun - whatever name you chose to call them, delicious they are!! commercial versions have fillings like chicken mushroom, chicken curry, teriyaki chicken or beef, shoyu chicken, purple yam (ube), pork hash (bola bola), and lup choung. some hawaiian manapua makers offer pizza filled, turkey melt, ham and cheese omelet, teriyaki burger and spicy sausage. imagine the flavors you could fill with your manapua. have fun and enjoy!! note: times do not include proofing time for the dough.", "55", "44", "['sprinkle yeast over 3 tablespoons lukewarm water and allow to stand until yeast softens', 'to remaining water , add oil or shortening , sugar and salt , stirring until melted or dissolved', 'cool', 'add yeast mixture', 'place flour in a large mixing bowl or a heavy-duty mixer and add most of the liquid', 'combine until flour incorporates liquid and starts to become a ball', 'add remaining liquid to make a very heavy dough', 'begin kneading the dough in the bowl', 'continue kneading until you have a smooth ball that is beginning to show signs of long strands on the outside , indicating that the gluten has fully developed', 'remove dough from bowl and rinse out bowl', 'pour sesame oil into bowl , return dough and turn it around until covered with a thin layer of the oil', 'cover with plastic wrap', 'allow to rise until double in bulk', 'place the dough in the refrigerator and allow it to rise', 'punch it down again and allow it to rise again', 'proceed with the filling while the dough is rising', 'in a saute pan , stir cornstarch , hoisin sauce , dry sherry , oyster sauce , ketchup , soy sauce and brown sugar into the 1 cup water until dissolved', 'bring to a boil , reduce heat and simmer 1 minute , stirring constantly', 'add char siu and simmer for 5 minutes', 'take off heat and allow to cool completely', 'chill covered until 1 hour before you are ready to stuff the manapua', 'allow to return to room temperature before using', 'when ready to cook , cut 12 squares of waxed paper and coat 1 side with very light coat of nonstick cooking spray', 'punch down dough and divide into 12 pieces', 'roll each into a ball', 'make the dough as thin as you can and try to keep the edges thinner than the center', 'place the circle of dough in the palm of your hand', 'spoon in a couple of tablespoons of filling , cupping the dough around it', 'with the thumb and finger of the other hand , pinch the edges of the dough as if you were making a fluted edging on a pie crust', 'pinch the folds together , twisting them as you do so', 'place the completed manapua on a square of greased waxed paper', 'allow to plump up into a globe with a taut exterior', 'heat a steamer with plenty of water or preheat an oven at 350 degrees f', 'if using steamer , fill steamer with manapua on their papers about 1 to 2 inches apart', 'cover and steam vigorously for 15 minutes', 'remove steamer from heat , let stand 5 minutes , then open', 'if using a metal steamer , place a folded tea towel across top of steamer , holding it in position with the lid', 'this will prevent steam from dropping onto manapua while steaming', 'if baking , place manapua on their papers on a baking sheet about 1-2 inches apart', 'brush top of buns with a little vegetable oil and bake 20 to 25 minutes', 'remove from oven and allow to stand 1 minutes', 'serve hot', 'manapua can be frozen', 'frozen bau may be reheated by wrapping with a paper towel and microwaving for 1 minute']", "16", "363.8", "9.0", "29.0", "18.0", "35.0", "7.0", "19.0", "None", "lunch", "kid-friendly", "None", "None", "None", "None", "None", "31.61");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("75227", "potato toast", "here's a nice snack to have when you want to eat potatoes! from the weekend magazine.", "60", "12", "['lightly dry roast the potatoes in a non-stick frying pan to dry out the water', 'heat 2 tsps', 'oil in a pan', 'add onions and green chillies to it', 'saute until the onions are softened', 'add the mashed potatoes , corriander and mint leaves , red chilli powder , knorr chicken stock powder and lime juice', 'mix well', 'remove from heat', 'spread a thick layer of potato mixture on each bread slice', 'brush over with the beaten egg', 'shallow fry in hot oil until lightly browned and cooked on both sides', 'serve hot with tomato ketchup !']", "11", "181.2", "5.0", "16.0", "9.0", "14.0", "4.0", "10.0", "None", "lunch", "None", "potatoes", "stove-top", "picnic", "eggs,potatoes,vegetables", "None", "38.91");
