INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("234453", "delicious nachos", "great quick snack and very delicious. my dh and i will often have these for lunches on weekends or when i just don't feel like cooking. i never measure what i'm putting on this; i just put what looks right - so my measurements are estimates. very easy to adjust to individual tastes - for example i don't like tomatoes and my dh doesn't like black olives so those two items get put on separate halves of the nachos.", "6", "10", "['spread the chips evenly on a plate', 'spread the beans on top of the chips', 'if you use refried beans , put little globs of them on the chips around the plate - these don't spread easily', 'cover with the cheese', 'sprinkle the chopped jalapenos over the cheese', 'microwave for 1 minute or until all the cheese is melted', 'spread the salsa over the nachos', 'spread the sour cream over the salsa', 'spread the avocado , black olives , and tomatoes over the sourcream', 'enjoy ! we usually end up making a second plate']", "9", "331.4", "37.0", "10.0", "42.0", "28.0", "57.0", "5.0", "None", "lunch", "beginner-cook,easy", "None", "microwave", "None", "None", "None", "35.41");
