INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("425059", "mini spinach feta calzone pockets no phyllo", "i'm on a spinach feta craze right now. i'm not fond of most phyllo doughs but loooove pizza dough. i like calzones that don't have ricotta. how to satisfy all three? this concoction that was a lot of fun to make! feel free to use another pizza dough recipe or a tube/bag of storebought dough, you need something that yields about one 12' pizza. some greek seasoning owuld also probably work great in this. a can with about a 5' diameter works as a nice cutter, so does a 10-oz ramekin or wide biscuit cutter. cook/prep time doesn't account for the pizza dough. depending on the dough yield and how much filling you put in each cutout, yields about 10-12 mini calzones.", "40", "11", "['run the spinach through a food chopper / processor until it has about the same consistency as tabouleh , then put into a mixing bowl', 'finely grate the feta cheese with a grater or food chopper then mix with the spinach', 'add all the other ingredients on the list except the dough to the bowl and mix thoroughly-- except the olive oil , make sure just 1 tablespoon of oil goes in the mix', 'roll the pizza dough onto a floured surface and make cutouts , about 10-12', 'spoon about 3-4 tablespoons of spinach mixture into the center of each cutout', 'place each cutout on a cookie sheet greased with cooking spray', 'fold the cutouts over so they resemble little calzones', 'make sure that there are no pointy edges or tops because they'll burn easily', 'with the remaining tablespoon of olive oil , brush the tops of the calzones', 'about 1 tablespoon should be enough to lightly brush them all', 'bake at 375f for 15-20 minutes or until golden brown on top']", "9", "48.7", "6.0", "1.0", "4.0", "3.0", "9.0", "0.0", "None", "lunch", "None", "None", "None", "None", "vegetables", "None", "37.46");
