INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("73443", "rolled stuffed eggplant aubergine", "take the work out of entertaining with this make-ahead main dish! original recipe by joanne scanapico with a few adapations of my own. it makes a 'delish' italian meal. prep and cook times are estimates since i haven't made it in awhile. been on diets...poor us!!", "90", "37", "['saute garlic in 1 tbls', 'of oil in large saucepan until golden brown , about 4 minutes', 'working in batches , combine crushed tomatoes , tomato sauce , salt , pepper , basil , oregano , and parsley in blender', 'whirl until smooth', 'pour into saucepan', 'add bay leaf', 'bring to boiling', 'lower heat', 'simmer , partially covered , 30 minutes', 'reserve', 'combine crumbs and flour in large shallow pan', 'beat eggs and milk in large bowl', 'peel eggplants', 'cut lengthwise into 1 / 8-inch-thick slices', 'dip into egg mixture', 'dredge with flour mixture , shaking off excess', 'set on wax paper', 'working in batches and using about 3 tbls', 'of oil per batch , saute eggplant in large skillet until golden , 1 minute per side', 'drain on paper toweling', 'combine ricotta , mozzarella , parmesan and parsley in bowl', 'mix well', 'spread 2 to 3 tbls', 'on one side of each slice of eggplant', 'roll up jelly-roll fashion', 'spread 1 cup tomato sauce over bottom of each of two 11 3 / 4'x7 1 / 2'x1 3 / 4' baking dishes', 'arrange rolls seam-side down in single layer in dishes', 'spoon sauce over rolls', 'save remaining sauce to pass', 'bake , covered in preheated 450 degree oven for 30 minutes or until sauce is bubbly', 'garnish with chopped parsley , if you wish', 'to make ahead: prepare rolls early in day', 'bake just before serving', 'or freeze', 'defrost in refrigerator overnight', 'bake as above', 'it's up to you !']", "19", "537.7", "46.0", "32.0", "33.0", "41.0", "51.0", "15.0", "None", "lunch", "one-dish-meal", "casseroles", "oven,stove-top", "dinner-party,easter,new-years,thanksgiving", "cheese,vegetables", "fall,winter", "35.75");
