INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("190225", "savory croissant breakfast pudding", "this is an emeril lagasse recipe that has rave reviews on the food network site; i just wanted to post it here so that i don't lose it. i'll be doing it for xmas morning; if anyone tries it before then let me know what you think! the recipe for his 'essence' seasoning is included, but it can be purchased at almost any grocery store nowadays, under emeril's label. add more veggies, omit meats for a tasty vegetarian option.", "65", "30", "['preheat the oven to 350 degrees f', 'lightly grease a 3-quart casserole dish and set aside', 'in a large skillet , melt 1 1 / 2 tablespoons of the butter over medium-high heat', 'add the onions , reduce the heat to medium , and cook , stirring , until soft and lightly caramelized , about 15 minutes', 'in a medium skillet , melt another 1 / 2 tablespoon butter over medium-high heat', 'add the canadian bacon and cook , stirring , for 3 minutes', 'drain on paper towels', 'add the sausage to the skillet and cook , stirring , until browned , about 5 minutes', 'drain on paper towels', 'in a large bowl , beat the eggs', 'add the milk , cream , salt , essence , and pepper and whisk to combine', 'add the croissants and let sit for 5 minutes', 'add the sausage , bacon , onions , fontina cheese , stir together , and let sit for 5 to 10 minutes', 'pour into the prepared dish and bake until almost completely set , 35 to 45 minutes', 'cover with aluminum foil if it's browning too much', 'in a small bowl , melt the remaining 2 tablespoons butter in the microwave , then combine with the bread crumbs and parmesan', 'uncover the pudding and evenly cover with the bread crumb mixture', 'return to the oven , increase the heat to 375 degrees f and bake until completely set in the center and golden brown on top , about 15 minutes', 'serve hot', 'emeril's essence creole seasoning :', '2 1 / 2 tablespoons paprika', '2 tablespoons salt', '2 tablespoons garlic powder', '1 tablespoon black pepper', '1 tablespoon onion powder', '1 tablespoon cayenne pepper', '1 tablespoon dried oregano', '1 tablespoon dried thyme', 'combine all ingredients thoroughly', 'yield: 2 / 3 cup']", "20", "828.7", "87.0", "54.0", "128.0", "71.0", "152.0", "15.0", "None", "breakfast", "None", "casseroles,pork", "mixer,oven", "None", "cheese,eggs,pork", "None", "25.41");
