INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("86428", "tex mex corn squares", "a great vegetarian recipe from homemakers' magazine. a bean, corn and salsa mixture on a polentalike base. the chipotles (smoked jalapeno peppers) can be quite hot! i often just add a dab of the peppers when cooking for folks who are not keen on spicy food. love the smokey flavour. edited:feb.08...we really enjoy this served with puppitypup's 'mexican night salad' (#188160)", "45", "12", "['preheat oven to 350', 'lightly oil an 11 x 7-inch baking dish', 'bring stock and salt to a boil', 'slowly whisk in cornmeal until mixture thickens', 'bring to boil', 'take off the heat', 'stir in 1 / 2 cup of the cheddar cheese and mix until melted', 'spread in prepared pan and cover to keep warm', 'combine beans , salsa , corn , chipotle peppers , chili powder and cumin in a bowl', 'stir in remaining cheese and coriander', 'spread mixture over cornmeal base', 'bake for 25-30 minutes-- until heated through and cheese is melted']", "11", "331.4", "16.0", "13.0", "32.0", "33.0", "31.0", "15.0", "None", "lunch", "beginner-cook,easy", "beans", "None", "None", "beans,vegetables", "None", "37.51");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("493702", "what a dal", "aromatic spices turn everyday beans and lentils into an exotic dish with the flavors of india. if a smoother texture is desired, puree up to two cups of the finished product in a blender or food processor and stir back into the pot. serve over freshly cooked basmati rice. recipe is from my fresh from the vegetarian slow cooker cookbook.", "500", "9", "['pour the oil into a 4-quart slow-cooker and set it on high', 'in a food processor , puree the onion , garlic , and ginger and add it to the cooker', 'cover and cook to mellow the flavor and remove the raw taste while you assemble the other ingredients', 'stir in the coriander , cumin , turmeric , cardamom , mustard , cayenne , and allspice , and cook , stirring for 30 seconds', 'turn the setting to low', 'add the lentils , kidney beans , and water', 'cover , and cook for 8 hours', 'add additional water , if needed', 'before serving , season with salt and pepper and adjust the other seasonings , if necessary']", "15", "287.8", "8.0", "13.0", "9.0", "33.0", "4.0", "14.0", "None", "lunch", "3-steps-or-less,easy", "beans", "crock-pot-slow-cooker", "dinner-party", "beans,vegetables", "None", "17.71");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("34331", "linguine with wild mushroom alfredo", "from vegetarian times, here is a new take on the classic alfredo sauce with 3 different kinds of mushrooms which makes a light but appetizing sauce. proof that an appetizing sauce does not have to be a ", "40", "12", "['cook linguine in 3 qts', 'boiling water until al dente , about 8-10 minutes', 'drain', 'in another large saucepan , heat the oil and wine over medium high heat', 'add mushrooms and garlic , and cook for 6-8 minutes , stirring often', 'stir in the milk , parsley , mustard , thyme , pepper , and salt', 'bring to a gentle simmer over medium heat', 'combine cornstarch and water until smooth', 'stir into the mushroom sauce and return to a gentle simmer', 'fold in the cooked linguine', 'cook for about 1 minute more over low heat , stirring frequently', 'transfer to warm serving plates and serve immediately']", "15", "357.6", "9.0", "42.0", "17.0", "30.0", "7.0", "20.0", "None", "lunch", "beginner-cook,easy,one-dish-meal", "None", "stove-top", "dinner-party", "vegetables", "None", "31.97");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("283440", "chicken parmigiana pie", "i wanted a parma pie without using bisquick (we don't have it here in oz)so went searching, i found this on a low-carb web site. i'm posting the recipe as written but i simply combined all the ingredients together,(except water)spooned into pastry, wacked them in the pie maker & voila, chicken parma pies!", "40", "7", "['heat oven to 200', 'c', 'spread cottage cheese on the bottom of a glass pie plate and then sprinkle with parmesan cheese', 'mix chicken & 3 / 4 cup mozzarella cheese with the herbs & tomato paste and then add eggs', 'mix cream with water and add to the chicken mixture', 'pour into pie plate and bake for 25-30 mins', 'top with remaining mozzarella and bake for 5 mins more']", "12", "263.8", "27.0", "9.0", "25.0", "39.0", "48.0", "1.0", "None", "lunch", "kid-friendly", "None", "None", "None", "cheese", "None", "21.48");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("15633", "sheer korma", "this special muslim dish is traditionally served on eid day (both eid al-fitr and eid al-adha) to the household after they come back from eid prayers. it is then served throughout the day to all well wishers and guests who come to celebrate eid with you. it is served by muslims from a variety of ethnic backgrounds, arab, asian, america, european, southeast asian, oriental, etc.. s", "140", "11", "['fry the vermicelli in the butter until well browned but not burnt', 'fry on low heat until all the butter is dried', 'add in 1 / 4 cup sugar and fry again', 'add in the whole milk cup by cup , stirring constantly , and bring to boil', 'add in the raisins , whole cardamon , and 1 / 2 cup slivered almonds , cashews and pistachioes , and the remaining sugar', 'reduce the heat and thicken the sheer korma to three-strand consistency , letting the milk boil until it is halved', 'the vermicilli must be very soft by now', 'quikly add in the rose water , charoli and fresh cream and let simmer covered for 10 mins', 'garnish with the saffron strands and powdered cardamom , and serve immediately', 'this special dish is served on the morning of eid day to all the household , after the eid prayer , as breakfast , and throughout the day to every guest that visits you', 'it keeps fresh for up to one week in the fridge']", "12", "447.4", "36.0", "181.0", "7.0", "30.0", "55.0", "15.0", "None", "breakfast", "easy,kid-friendly", "None", "stove-top", "dinner-party,ramadan,wedding", "fruit", "fall,spring,summer,winter", "31.6");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("99982", "meatballs with parmesan and sausage", "tradtional, italian style, juicy meatballs. this can be cooked with beef, turky, chicken etc etc mince. it just depends on what your tastes are! this is a fab recipe!serve it with a simple sauce ( may i suggest my simple italian sauce ) as the meatballs have a flavour all of their own.", "75", "5", "['in a bowl soak the bread in the milk until it soaks up most of milk', 'once bread has done this , remove from bowl , disgard milk , roughly brake bread apart and place all of the other ingredients in a bowl with the bread and mix together', 'roll the mixture into small / medium sized balls with your hands', 'brown meat balls in pan with oil then place into oven for 10 minutes at 225 degrees', 'add to a great italian sauce and simmer for 60 minutes']", "14", "61.4", "6.0", "0.0", "4.0", "8.0", "7.0", "0.0", "None", "lunch", "easy,kid-friendly", "pork", "None", "dinner-party", "beef,pork", "None", "16.62");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("298241", "vegetarian black bean hamburger tacos", "this recipe uses mexican flavored vegetarian hamburger and it is very good. you may cook your own black beans but to simplify the recipe i used a 16oz can of black beans. be inventive and add your own choice of vegetables. your shells should be heated but watch they burn very quickly. i like to put everything on the table and let folks build their own so the shells stay crisp and the ingredients hot. if you are making tostados add more black beans , burger etc to the shell because 1 tostado is a whole meal. this recipe makes 6 tacos or 4 tostados", "35", "9", "['over medium heat fry the garlic & peppers in the olive oil for 3 minutes', 'add drained beans and mash them , mixing in the peppers & garlic ,', 'add bean juice as needed for a nice consistency', 'mix well & stir frequently', 'heat the vegetarian hambugerr in a skillet over medium heat , stir frequently', 'when the beans are ready , the burger heated through assemble your tacos', 'put 3 tbsp of beans in the bottom of the shell , add 3 tbsp burger , then the lettuce , tomatoes , cheese and salsa', 'top with sour cream and sliced avocado or serve your avocado on the side', 'enjoy']", "12", "555.5", "40.0", "24.0", "40.0", "56.0", "69.0", "18.0", "None", "lunch", "easy", "beans", "None", "None", "beans,cheese", "None", "20.34");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("286514", "okara hummus", "since buying a soy toy soymilk maker about a year and a half ago, i have been an avid user of recipes that include okara, which is the by-product that is leftover from the soymilk making process. this recipe comes from the cookbook that was included with the soy toy. it's a delicious--and nutritious!--way to include more soy in your diet.", "15", "5", "['saute the garlic cloves in olive oil until golden brown', 'add the chickpeas to a food processor or blender and blend until smooth', 'add all of the other ingredients', 'adjust the seasonings to taste', 'serve with raw vegetable crudites , crackers , or toasted pita points']", "7", "112.0", "10.0", "0.0", "5.0", "5.0", "4.0", "3.0", "None", "lunch", "beginner-cook,easy", "beans", "food-processor-blender", "None", "beans,soy-tofu", "None", "24.04");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("372621", "asian style sweet sour meatballs", "this is a recipe i made my own version of sweet sour sauce. so delicious!! this is good for side dish.", "45", "5", "['mix all the ingredients of meatballs with plastic gloves and make meatballs ,', 'fry the meatballs with olive oil', 'put all sauce ingredients and boil it in a pot', 'when it's boiled you can put the prepared meatballs in the sauce and boil down until when it become a little sticky', 'you can put them on the dish and enjoy with rice']", "12", "409.7", "31.0", "57.0", "78.0", "60.0", "37.0", "8.0", "None", "lunch", "None", "None", "None", "dinner-party", "beef", "None", "19.61");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("82170", "lamb or beef kebab pitas with tzatziki", "because of the use of ground meat, this is what we call a kebab here. delicious! they can be oven-roasted, but i highly recommend you do these on the bbq.", "50", "12", "['in a bowl , knead together all the kebab ingredients', 'form into sausages of about 4-5 inches in length', 'cover and leave in the fridge for about an hour', 'prepare the tzatziki: in a bowl , whisk together all the ingredients', 'taste , and adjust seasoning', 'refrigerate for 30 minutes', 'preheat oven to 400f', 'grill the kebabs until brown and crusty on all sides , and cooked through , about 12-14 minutes', 'get some grilled pita breads', 'place 1 or 2 kebabs inside each pita and add some slices of tomato , onion , some tzatziki , and a generous sprinkling of parlsey', 'roll up as tightly as you can without mushing up your sandwich , and wrap a piece of aluminum foil around bottom to catch any drips', 'enjoy one of the best sandwiches you've ever had']", "22", "479.0", "59.0", "6.0", "6.0", "44.0", "67.0", "3.0", "grilling", "lunch", "kid-friendly,one-dish-meal", "sandwiches", "None", "None", "lamb-sheep", "None", "21.05");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("512678", "mini vegetable frittatas", "this is rustic italian recipe adapted from an old immigrant cookbook. simple ingredients that combine to make delicious sandwich sized frittatas. these are amazing eaten as a warm sandwich with olive oil-drizzed, grilled italian bread. enjoy!", "50", "11", "['preheat oven to 375 degrees f', 'oil 2 - 12 muffin cup tins with olive oil', 'saute the onion and garlic in the olive oil and butter until onion is clear', 'add artichoke hearts , mushrooms and zucchini', 'saute 5 to 10 minutes', 'spoon this mixture into muffin tins about half way up', 'beat the eggs with the salt , pepper and herbs until frothy', 'mix in the grated cheese , then pour evenly over the top of the vege mixture', 'top each with a tablespoon of the marinara sauce and bake 15 minutes , or until egg mixture is set', 'let cool for 5 minutes', 'use a knife to loosen the edges of each frittata and serve']", "13", "84.6", "8.0", "5.0", "9.0", "9.0", "11.0", "1.0", "None", "lunch", "None", "sandwiches", "None", "None", "eggs,vegetables", "None", "34.77");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("172647", "easy crust pizza", "from the back of a pillsbury flour bag - a no knead/no toss pizza crust that even those of us dough challenged cooks can make. even though not mentioned in the ingredients, i think it needs some minced garlic.", "50", "14", "['heat oven to 425', 'grease a 12-inch pizza pan', 'lightly sprinkle with cornmeal', 'in a skillet , brown the ground beef', 'drain off fat', 'in a medium bowl , combine flour , italian seasoning , salt , pepper , milk & eggs', 'blend until smooth', 'pour batter in pizza pan , tilting to cover bottom evenly', 'spread beef over batter', 'top with mushrooms & onions', 'bake on lowest oven rack for 20-25 minutes or until golden brown', 'remove from oven', 'pour pizza sauce evenly on top & sprinkle with cheese', 'return to oven for another 7-12 minutes']", "12", "363.7", "29.0", "6.0", "15.0", "49.0", "41.0", "7.0", "None", "lunch", "None", "pizza", "None", "None", "None", "None", "38.74");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("182058", "the authentic greek gyro a greek grill party", "inspired by peacefulnightdoves lovely & yummy looking photo today of easy beef gyros by mamas kitchen & because beef is so expensive here in iceland, i went looking for the classic gyro using lamb cubes  only to find it hasnt been entered here on rz. i decided to chg that & found this recipe at recipeland.com. its simple title was greek gyro, but its way more than that  its the authentic greek gyro - a greek grill party (even if served inside). its clearly more work than hybrid versions & requires planning, but i bet its well worth the effort  esp if youre able to get the real thick greek yoghurt & eliminate prep step #3. (time was given by the site & didn't include marination, but did include time to make the yoghurt which can be eliminated.) i hope you enjoy these!", "90", "23", "['the morning before an afternoon or evening bbq , make marinade by combining oil , lemon juice , garlic cloves , herbs & pepper in a med bowl', 'set aside about 1 / 4 cup of the marinade to mix w / the onions', 'place cubed lamb in bowl w / remaining marinade & toss to coat well', 'refrigerate lamb & marinade reserved for onions', 'the morning of the bbq , place onions in small bowl & toss w / reserved marinade', 'cover w / plastic wrap , refrigerate several hrs & stir to mix well several times', 'make yoghurt sauce by lining a med bowl w / cheesecloth & spooning in the yoghurt', 'tie up edges of cheesecloth w / string to form a pouch', 'suspend over a bowl or place in a colander over a bowl & let stand to drain several hours', 'moisture will drip from the yoghurt & give it a thick consistency', 'mix yoghurt w / minced garlic , herbs , lemon juice , oil , salt & pepper', 'cover & chill', 'when ready to bbq , stack pita bread in 2 piles of 6 & wrap each pile in foil', 'heat coals or gas grill', 'remove meat from marinade & reserve marinade for basting', 'thread lamb onto greased skewers & grill over greased grill about 4 in from hot coals', 'cook about 10 min or till lamb is brown on the outside , but still pink inside', 'warm pita rounds over barbecue', 'before serving , lift onions from the marinade w / a slotted spoon & place in a serving bowl', 'cut whole tomatoes into thin wedges & place on a serving plate w / the lettuce leaves', 'set out yoghurt sauce & lots of napkins', 'to serve , cut each warmed pita round in half & separate to form 2 pockets', 'each person fills a warmed pita picket with some of the grilled lamb cubes , lettuce , tomatoes & onions , then tops with a spoonful of the sauce']", "15", "381.8", "24.0", "19.0", "15.0", "36.0", "24.0", "13.0", "grilling", "lunch", "None", "sandwiches", "None", "dinner-party", "lamb-sheep,vegetables", "None", "16.09");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("34787", "crispy burritos sandwich maker", "in response to sandwich maker recipes.", "8", "7", "['preheat sandwich maker', 'lightly mist wells with non-stick cooking spray', 'place a tortilla in the sandwich maker , top with 1 tbsp', 'each: meat , beans , salsa , and cheese', 'top with remaining tortilla', 'close and cook for about 3 minutes', 'serve with a dollop of sour cream and some shredded lettuce']", "5", "187.2", "7.0", "4.0", "15.0", "9.0", "5.0", "10.0", "None", "lunch", "None", "sandwiches", "None", "None", "None", "None", "12.85");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("346068", "spiced persimmon bread", "a wonderful, moist, sweet, and flavourful loaf that is a nice alternative to banana or pumpkin bread. it's great with coffee on a winter morning or as an afternoon snack. i think this would make really good muffins, as well. be sure to only use soft and ripe persimmons. from 'diana's desserts'.", "90", "12", "['preheat oven to 350 degrees f', 'grease and flour a 9 x 5-inch loaf pan', 'line pan with parchment paper if desired and grease parchment paper also', 'in a mixing bowl , sift together the flour , ground cinnamon , ground cloves , ground nutmeg , ground ginger , baking poder and baking soda', 'set aside', 'in the bowl of an electric mixer , food processor or blender add eggs , butter , buttermilk , lemon juice , mashed persimmons , sugar and salt', 'mix or blend until smooth', 'slowly add reserved flour mixture and mix until well incorporated', 'by hand , fold in nuts', 'pour batter into prepared loaf pan and bake in preheated oven for 75 to 85 minutes , or until toothpick inserted into the center of bread comes out clean', 'transfer to wire rack and let cool in pan for 10 minutes before turning out onto rack to cool completely', 'once cooled , dust top with confectioners' sugar if desired']", "16", "206.4", "10.0", "68.0", "6.0", "6.0", "19.0", "11.0", "None", "breakfast", "beginner-cook,easy,kid-friendly", "None", "oven", "None", "eggs,fruit", "fall,winter", "19.67");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("303127", "herby tuna burgers with wasabi low fat and healthy", "these tuna burgers are a great way to get people that don't think they like fish to eat and enjoy it. these burgers are very low fat, particularly for a burger, easy to make and taste really good. the wasabi paste i used didn't overpower the other flavors but added a nice background taste but if you wanted a stronger strength you could certainly use more of the paste. the cooking time includes the 30 minute chilling time.", "45", "13", "['put the onion , garlic , parsley , cilantro and soy sauce into a food processor or mini chopper and blitz together for about 20 to 30 seconds until everything is blended and the herbs are finely chopped', 'empty the herb mixture into a mixing bowl and set aside', 'put the tuna chunks into the food processor and blitz until the tuna is ground up but not yet a paste', 'this takes me about 30 seconds with the need to pulse a bit to get the stubborn chunks', 'scrape the tuna out into the same mixing bowl you put the herb mixture into', 'add the wasabi paste into the bowl and them combine with your hands to make sure everything is well mixed', 'divide the mixture into four and make into four even sized patties', 'put patties on a plate and chill in the fridge for 30 minutes', 'take the burgers out of the fridge and heat a non-stick frying pan until it is hot and use either a bit of canola oil or a cooking spray if you want to save those extra fat grams', 'though , if you keep it canola oil it is mostly unsaturated fat', 'so , whatever works for you', 'put the burger patties in the hot pan and cook for about 5 minutes on each side until they are cooked through', 'serve']", "7", "182.2", "8.0", "4.0", "14.0", "55.0", "7.0", "1.0", "None", "lunch", "easy", "sandwiches", "food-processor-blender", "None", "None", "summer", "17.47");
