INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("161872", "angels easy breakfast burritos", "i ate a similar burrito to this at sonic and loved it. i was suprised that for a little burrito it filled me up. this recipe is large because i love to make these and freeze them for those mornings when i am in a hurry, but need to eat.", "40", "16", "['heat oven to temp as instructed on package', 'line a baking sheet with foil then spray with cooking spray to prevent sticking', 'lay tater tots on pan and sprinkle with salt', 'cook until done , set aside to cool completely', 'brown sausage in a skillet until done', 'drain to remove fat then set aside to cool completely', 'scramble eggs and cook until desired doneness', 'set aside to cool completely', 'once all cooked ingredients are cooled completely , begin making burritos', 'lay sausage , then eggs , then cheese , then tater tots on a tortilla', 'roll tortilla into a burrito', 'set aside', 'once all burritos are rolled , cover each in plastic wrap', 'use a freezer safe plastic bag to store burritos inches', 'use salsa when you reheat burritos', 'burritos take 2-3 minutes to reheat , depending on your microwave']", "8", "513.6", "57.0", "3.0", "40.0", "39.0", "64.0", "7.0", "None", "breakfast", "beginner-cook,easy,kid-friendly,toddler-friendly", "pork", "None", "None", "eggs,pork", "None", "22.13");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("16864", "greek chicken with capers raisins and feta", "not only quick and delicious but low cal too!! from cooking light. serve with curried couscous (recipe #16866), and a light green salad.", "30", "13", "['place each chicken breast half between 2 sheets of plastic wrap', 'flatten to 1 / 4 inch thickness using a meat mallet or rolling pin', 'combine the flour and oregano in a shallow dish , dredge chicken in flour mixture', 'heat oil in a large nonstick fry pan over medium high heat', 'add chicken and cook for 4 minutes on each side', 'remove chicken from pan , keep warm', 'add onions to pan , saut 2 minutes', 'stir in broth , raisins , and juice , cook 3 minutes , scraping pan to loosen browned bits', 'return chicken to pan', 'cover , reduce heat , and simmer for 8 minutes or until chicken is done', 'place chicken on individual plates and keep warm', 'stir capers into sauce , and top each chicken breast with 1 / 3 cup sauce and 1 tablespoon cheese', 'garnish with lemon slices if desired']", "11", "249.9", "10.0", "37.0", "12.0", "57.0", "11.0", "5.0", "None", "lunch", "easy", "None", "stove-top", "dinner-party", "cheese,chicken,fruit", "fall,spring,summer,winter", "19.56");
