INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("118241", "seared italian marinated breast of chicken with tomato and basil", "in italian it's petto di pollo marinato e saltato con pomodoro e basilico! simple but full of flavor, this dish is a home cook's dream! the chicken can marinate overnight and the sauce can be made early in the day. serve over pasta for a less formal meal, or at room temperature for an easy summer lunch! delicious! (note: cook time does not reflect marinating time of at least 8 hours). from lidia bastianich.", "65", "17", "['to make the marinade , in a small bowl , combine the olive oil , garlic , rosemary , sage , and salt and pepper to taste', 'slice the chicken breasts on the diagonal into thirds', 'using a large knife or cleaver , lightly pound each piece to flatten slightly', 'place in a shallow glass or ceramic dish', 'pour the marinade over the chicken , cover , and refrigerate for at least 8 hours , turning occasionally', 'to make the sauce , in a medium-sized saucepan , heat 3 tablespoons of the olive oil over moderate heat', 'add the garlic and saute for about 4 minutes , or until lightly browned', 'stir in the tomatoes with their juices , the red pepper flakes , and salt and pepper to taste', 'simmer for 5 minutes', 'stir in half of the basil leaves', 'remove from the heat and cover to keep warm', 'heat a large saute pan over medium-high heat until very hot', 'drain the chicken , discarding the marinade', 'add the chicken to the hot pan , without crowding , and saute for 5 to 10 minutes , or until golden brown on both sides and just cooked through', 'transfer to a large , warm serving platter', 'drizzle the remaining 1 tablespoon olive oil over the tomato sauce and pour over the chicken', 'sprinkle the remaining basil over the top and serve']", "9", "430.1", "44.0", "8.0", "4.0", "72.0", "21.0", "1.0", "None", "lunch", "None", "None", "None", "dinner-party", "chicken,vegetables", "None", "10.51");
