INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("501436", "greek pasta with meatballs", "recipe is from cooking light magazine, nov. 2009. unlike traditional spaghetti, this recipe uses orzo pasta , ground lamb and feta cheese.", "38", "21", "['preheat oven to 375 degrees f', 'cook orzo pasta according to package directions', 'drain', 'keep warm', 'combine breacrumbs and next 6 ingredients in a medium bowl', 'stir in 1 1 / 2 tablespoons parsley', 'add egg whites , stirring mixture until just combined', 'shape mixture into 12 meatballs', 'cover and chill meatballs 5 minutes', 'heat oil in a large ovenproof skillet over medium-high heat', 'add meatballs to pan', 'cook 8 minutes , turning to brown on all sides', 'drain well', 'wipe pan clean with paper towels', 'return meatballs to pan', 'spoon marinara sauce over meatballs', 'sprinkle with cheese', 'bake at 375 degrees f', 'for 11 minutes or until meatballs are done', 'sprinkle with remaining 1 1 / 2 teaspoons parsley', 'serve over orzo']", "13", "881.0", "60.0", "61.0", "48.0", "78.0", "86.0", "29.0", "None", "lunch", "easy,kid-friendly", "None", "None", "dinner-party", "lamb-sheep,vegetables", "None", "13.33");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("302467", "easy grilled burgers italiano for the bbq", "only a few ingredients necessary which you probably have available. i usually have dried basil but this calls for fresh. if you use dried, use a little less. from first magazine, july, 2007. i haven't tried it yet. lettuce, tomatoes, pickles and onions are suggested as possible garnishes.", "25", "5", "['heat grill', 'in a bowl , mix all the ingredients , except for the rolls , until just blended and shape into 6 patties', 'grill 12 minutes or until an instant read thermometer inserted in center of the burgers reads 160 degrees f', ', turning once', 'place grilled patties on rolls and garnish as desired']", "7", "528.9", "42.0", "4.0", "54.0", "74.0", "53.0", "10.0", "grilling", "lunch", "easy", "sandwiches", "None", "None", "beef,cheese", "summer", "33.08");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("288345", "indian restaurant style onion bhajia deep fried onion fritters", "an absolute must as an accompaniment to curry or wonderful as a starter for an indian meal. onion bhajis are tasty little onion balls bound together with lightly spiced and fragrant chickpea flour batter. serve these with coriander chutney or cucumber raita. you will always be offered these on any british indian restaurant menu; they also make great snacks if served with salad and assorted chutneys and spicy dips. you can lighten these up a little bit by frying them, and then allowing them to drain over a wire rack before reheating them in a medium to hot oven for about 3 to 5 minutes.", "20", "9", "['put the vegetable oil into a large pan and heat over a medium to high heat', 'make the batter: in a large bowl , add the chickpea flour , baking powder , rice flour , turmeric , black onion seeds , chili powder , cumin , coriander , salt and the water', 'mix together well and then add the sliced onion - mix the onion through until well coated', 'check the temperature of the oil by dropping a small cube of bread in - it should brown fairly slowly - not burn or go dark brown straight away', 'carefully drop spoons of the bhaji batter in to the oil , do not overcrowd as they will stick together', 'fry them in small batches for 1 to 2 minutes until they are a golden brown and crispy', 'remove them with a slotted spoon and allow them to drain on a paper covered plate in a warm oven', 'repeat until all the bhaji batter is finished and they are all cooked', 'sprinkle over a little salt and serve straight away with coriander chutney or cucumber raita']", "12", "1691.4", "257.0", "34.0", "5.0", "24.0", "108.0", "13.0", "deep-fry", "lunch", "easy", "None", "None", "dinner-party", "vegetables", "None", "18.59");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("404755", "beijing style pork shrimp jiaozi chinese dumplings", "i got this recipe from my old land lord in beijing. he was a 'lao beijing ren' (native beijinger), so his recipes are very authentic. he decided that my roommate and i needed to learn all the beijing dishes, and thus took it upon himself to teach us. my friends and i like to have dumpling parties, where we get together and wrap dumplings and talk, which makes it not as labor intensive.", "100", "24", "['clean the cabbage , chop it , and put it in a large non-reactive pot with salt set it aside and let it sweat out water for 45 min or so', 'if you're using a meat grinder , feed the shrimp , pork , garlic , onions , and ginger through the meat grinder , getting everything to be a smooth , yet chunky mixture', 'if you're using a food processor , you're going to half way freeze the meat , and chop each ingredient on it's own , and then mix them in a pasta bowl', 'traditionally , all these ingredients are minced and chopped using a large cutting board and a cleaver', 'however you do it , it's up to you', 'put the meat and veggie mixture into a a large bowl', 'add the salt , soy sauce , egg and sugar to the mixture', 'in a heavy skillet , heat up the oil', 'add the spice powders and toast the spices', 'pour this mixture hot over the meat mixture in the bowl , and stir inches', 'drain the cabbage , rinse it and drain again', 'mix it in to the meat mixture', 'make the dumpling wrappers: mix together 1 1 / 2 cup flour and pinch of salt and then slowly add just enough water to result in a springy dough', 'knead it a lot to make the gluten stringy and strong', 'roll the dough out into a long rope , cut the rope apart into ping pong ball sized pieces', 'flatten the pieces out into small flat discs , and flour a table or cutting board , and roll them out using a beer bottle or small rolling pin', 'as you make them , hand them to the person wrapping the dumplings', 'or just buy the pre-made wrappers', 'wrap the dumplings in the dumpling skins , pinching together the sides and tucking in the ends', 'cook the dumplings: boil a pot of water', 'drop the dumplings in , they are ready when the float to the top of the pot', 'don't boil too many at once , or they'll stick together', 'scoop them out with a strainer , so you can use the water for the next batch', 'serve with soy sauce , dipping sauce , raw garlic , chili sauce , or cut up green onions']", "15", "1489.9", "46.0", "12.0", "188.0", "119.0", "43.0", "79.0", "None", "lunch", "None", "pork", "None", "None", "pork", "None", "29.29");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("13076", "yaprach stuffed grape leaves", "this recipe was given to me by my upstairs neighbor. my daughter enjoyed it so much my neighbor always used to make extra just to give to her. now that we have the receipe my daughter makes it herself.", "150", "14", "['mix all the ingredients except for the grape leaves together in a large bowl', 'rinse the grape leaves well in hot water and leave them in a bowl of hot water while you work with each one individually', 'place one leaf on your working surface , smooth side down', 'take one teaspoon of rice filling and place it in a finger shape in the middle of the leaf', 'roll the outer edges towards but not quite reaching the middle', 'roll from the bottom towards the top into a cigar shape and place in a large pot', 'continue until all the leaves and filling has been used up , placed the filled leaves in the pot in a circular fashion , one level on top of another', 'add water until slightly covering the leaf layers', 'add a few drops of oil , a pinch of pepper and citric acid and 1 / 2 tablespoon of tomato paste to the water', 'boil covered until all the water has been absorbed', 'remove from heat', 'place a kitchen towel between the pot and cover , cover well and return to a small flame', 'cook for an additional 1 / 2 hour', 'serve hot or cold']", "9", "94.0", "0.0", "3.0", "3.0", "3.0", "0.0", "6.0", "None", "lunch", "easy", "None", "None", "christmas", "berries,chocolate,fruit", "None", "18.28");
