INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("13586", "antipasto", "the best antipasto i've ever come across, the recipe is requested with every serving. wonderful on crackers and have used it as a cold topper for a brie cheese wheel. (originally from ", "70", "12", "['chop green peppers , cut carrots into julienne strips', 'chop ripe olives , green olives , pickled onions , pimento , sweet pickle and set aside', 'break cauliflower into bite sized pieces', 'put 2 oz of olive oil into frying pan and add 2 cloves garlic , add cauliflower and saute', 'remove from heat and set aside', 'in a large pot add remaining olive oil and saute the remaining 3 cloves of garlic , add ketchup , wine , tomato paste and oregano', 'add chopped vegetables , pickles , olives , artichokes , pimento , cauliflower and mushrooms', 'simmer for 10 minutes , stirring frequently', 'add anchovies and tuna to mixture and bring to a boil , stirring constantly to prevent sticking', 'serve for use immediately , refrigerate for up to 2 weeks , place in containers for freezing or place immediately in hot sterilized jars', '[editors', ']']", "19", "513.6", "35.0", "179.0", "87.0", "35.0", "17.0", "21.0", "None", "lunch", "None", "None", "stove-top", "christmas,dinner-party,easter,new-years", "vegetables", "winter", "27.97");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("78612", "hummus with raspberry vinegar", "my dad sent me this recipe when i told him i was going to the mediterranean deli almost everyday for hummus! i leave out the cilantro entirely when i make it, and i play around with different flavored vinegar or oil - my favorite is to use chili-flavored vinegar and balsamic vinegar! experiment and let me know how it turns out!", "17", "9", "['heat oil in a nonstick skillet over medium-high heat', 'add onion , and saut 5 minutes or until onion begins to brown', 'add vinegar , bring to a boil , and cook 2 minutes or until vinegar evaporates', 'cool to room temperature', 'drain chickpeas , reserving cup liquid', 'place chickpeas and chopped cilantro in a food processor , and process until mixture resembles coarse meal', 'add onion mixture , cup reserved liquid , cumin , pepper , salt , and process until smooth', 'garnish with cilantro sprigs if desired', 'per serving : 44 calories , 2 grams protein , 1 gram fat , 7 grams carbohydrates , no cholesterol , 75 milligrams sodium , 1 gram fiber']", "9", "375.0", "14.0", "20.0", "51.0", "24.0", "6.0", "20.0", "None", "lunch", "beginner-cook,easy", "beans", "food-processor-blender", "dinner-party", "beans", "None", "39.47");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("258187", "kimchi pancakes kimchi jun", "i buy kimchi by the gallon at our local korean market, but you can even buy it at walmart on the fresh vegetable isle. these are great as a snack or part of a korean dinner. be sure to dip them in a good soy sauce dipping sauce! yum!", "15", "6", "['mix flour , water and salt together and combine with the rest of the ingredients to create a thick 'pancake' batter', 'heat 2 tablespoons oil', 'place 1 / 4 of the batter in the pan to form a pancake', 'fry until both sides are golden', 'reduce heat to low and cook all the way through', 'repeat with remaining portions']", "7", "135.6", "3.0", "0.0", "12.0", "6.0", "1.0", "8.0", "None", "lunch", "3-steps-or-less,easy", "None", "None", "None", "None", "None", "15.27");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("177728", "spring onion and feta noodle cakes", "this is a recipe for a basic noodle cake. on their own they are a little bland (i still like to eat them plain sometimes)they can be easily jazzed up by serving with a dipping sauce or adding garlic, chilli anything you like really!", "25", "5", "['break up noodles into shorter lengths and boil in lightly salted water for 3 minutes remove , drain and rinse in cold water', 'mix egg , flour and milk to make a batter add the onions , feta and noodles and mix together', 'heat some oil in a pan', 'fry each cahe for about 2 mins each side giving them a press down after the first turning', 'eat hot or cold']", "7", "204.0", "11.0", "5.0", "12.0", "18.0", "22.0", "8.0", "None", "lunch", "None", "None", "None", "dinner-party", "cheese,vegetables", "None", "18.83");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("125943", "chocolate hazelnut phyllo dessert fit for queens", "this is a perfect recipe for people who have never used phyllo. some people make it sound as though if you break a sheet the whole dish is broken. sheets can be broken and it's still 'all good' and elegant.", "29", "19", "['preheat oven to 375f', 'measure nutella , corn syrup , and cinnamon sugar in measuring cups', 'set aside the measuring cups', 'melt butter and set aside with a pastry brush in it', 'unroll 1 sleeve of defrosted phyllo dough on work space', 'grease a square baking pan with butter', 'now pick up a sheet of phyllo dough and put it into the bottom of the pan', 'try to work fast because it's easy', 'use up 5 of the sheets this way', 'layer the next 5 sheets by sprinkling cinnamon sugar on top of the butter', 'put a sheet down and butter it', 'next , put a sheet down and brush it with butter and while holding down the phyllo sheet spread all of the nutella on the sheet', 'don't worry about getting it perfectly smooth and even', 'use the rest of the sheets by alternating buttered sugar cinnamon layers and buttered corn syrup drizzled layers', 'when doing the corn syrup , use the amount you think you would like', 'brush the top with butter and sprinkle cinnamon sugar and drizzle a tiny bit of corn syrup', 'cook in oven on the bottom rack for about 9 minutes or until the top layer is nice and brown', 'cut and eat !', 'this dessert's quality is compromised after a couple hours so you really shouldn't plan on storing it']", "5", "348.9", "27.0", "68.0", "10.0", "8.0", "37.0", "14.0", "None", "lunch", "5-ingredients-or-less,beginner-cook,easy,kid-friendly", "None", "microwave,oven", "dinner-party", "chocolate", "None", "27.75");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("10902", "marinated octopus", "this is an adopted recipe and should be made with larger, older, not itty bitty baby octopus. the flavour of vinegar and lemon here are strong and not to everyone's taste. if you are fond of tako sashimi, this is not for you. but if mediterranean fish salads are your thing, this is authentic.", "125", "9", "['place octopus in a large pan with plenty of water and boil for 2 hours', 'remove from heat', 'wash and remove the skin', 'cut in desired size pieces', 'in a bowl , combine all the ingredients , except the lemon juice , and add octopus', 'mix well', 'cover', 'and refrigerate for several hours or overnight', 'when ready to serve , stir in the lemon juice']", "8", "250.8", "41.0", "4.0", "0.0", "0.0", "18.0", "1.0", "None", "lunch", "beginner-cook,easy,kid-friendly", "stews", "refrigerator,stove-top", "dinner-party,picnic,wedding", "None", "None", "16.7");
