INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("428044", "greek grain salad", "recipe found on foodnetwork.com and it is very highly rated. perfect main course for a vegetarian or side dish for a summer bbq! the original recipe called for a crazy amount of grains, so it has been revised taking other recommendations.", "60", "11", "['make the salad: bring a large pot of water to a boil', 'add the barley and cook for 15 minutes', 'using a sieve , remove barley from the water and transfer to a bowl', 'bring the water back to a boil and salt , to taste', 'add the barley and rye and wheat berries and cook , stirring occasionally , until tender , about 30 to 40 minutes', 'drain , transfer to a bowl , and cool', 'when cool , combine with the remaining ingredients', 'meanwhile , make the dressing: whisk together the vinegar , dill , and garlic', 'while whisking , drizzle in the oil to make a smooth dressing', 'season with salt and pepper , to taste', 'toss the salad with the dressing and serve']", "15", "244.9", "20.0", "6.0", "9.0", "12.0", "17.0", "8.0", "None", "lunch", "None", "None", "None", "None", "grains", "summer", "29.64");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("328705", "pasta with crunchy crumbs and feta for one", "a simple pasta dish but with more of a bite! you get to use up old bread so you'll save more money. serves one but easily doubled, tripled etc.", "15", "7", "['pour boiling water into a large pan , add some salt and the pasta and cook according to the pack instructions', 'meanwhile , heat the butter in a small saucepan and when sizzling add the breadcrumbs and cook for about 5 mins , stirring occasionally until crisp and golden', 'use a zester to grate the lemon zest into the crumbs', 'cut the lemon half into two wedges and set aside', 'drain the pasta , put it back in the pan , toss in the crumb mixture , add the feta cheese chunks , parsley and seasoning', 'serve in a large warm bowl drizzled with olive oil and with the lemon wedges on the side', 'squeeze lemon juice over as you eat the pasta to bring out the flavour']", "8", "800.8", "63.0", "22.0", "46.0", "48.0", "124.0", "29.0", "None", "lunch", "easy,kid-friendly", "None", "None", "None", "cheese", "None", "16.37");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("389955", "baked taquitos", "i got this recipe off of a blog (bigboldbeautifulfood.blogspot.com). i have made it once and the flavour was really good, but it looked funny! the instructions are long but it is actually very simple and quick to make. i am posting how the recipe is originally written (well almost) but you might want to add a bit more spice. i served it with a spicy salsa and it tasted great. the chicken broth is needed if you are using corn tortillas only.", "30", "25", "['preheat oven to 425f and oil a baking sheet', 'heat olive oil over medium heat in a skillet', 'add onions and cook until starting to turn translucent , 3-5 minutes', 'add garlic , cumin , paprika , and salt', 'stir to combine and cook 2 to 3 minutes longer , until fragrant', 'take from heat and stir in cilantro and chicken', 'set aside', 'if you are using flour tortillas:', 'lay tortilla on work surface and spread 2 or 3 tablespoons of chicken mixture over bottom third', 'top with cheese and roll up', 'place seam side down on the baking sheet', 'repeat with remaining tortillas', 'spray or brush tortillas lightly with oil', 'bake until crisp and lightly browned , 12-15 minutes', 'serve immediately', 'if you are using corn tortillas', 'bring chicken broth to a simmer in a skillet or pot wide enough to hold tortillas', 'dip tortillas one at a time , briefly , just to soften , 1-2 seconds', 'lay tortilla on work surface and spread 2 or 3 tablespoons of chicken mixture over bottom third', 'top with cheese and roll up', 'place seam side down on the baking sheet', 'repeat with remaining tortillas', 'spray or brush tortillas lightly with oil', 'bake until crisp and lightly browned , 12-15 minutes', 'serve immediately']", "11", "1686.4", "77.0", "47.0", "143.0", "152.0", "59.0", "76.0", "None", "lunch", "None", "None", "None", "None", "chicken", "None", "12.96");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("469708", "tandoori shrimp", "recipe is from the celebrate chicago cookbook, junior league of chicago. love the aroma and flavor of garam masala - this sounds like a great grilling recipe. prep time does not include marinating time in refrigerator.", "37", "13", "['cook cumin seeds in a small skillet over medium-high heat 3 minutes or until seeds are lightly toasted', 'remove from heat , and let cool', 'peel shrimp , and devein , if desired', 'cut a slit almost through back of each shrimp', 'place shrimp in a large heavy duty , zip-top plastic bag', 'combine cumin seeds , chilies , yogurt , lemon juice , red pepper , salt , pepper , garam masala and garlic', 'pour over shrimp', 'seal bag securely , and shake until shrimp are well coated', 'marinate in refrigerator 4 hours , turning bag occasionally', 'remove shrimp from marinade , discarding marinade', 'thread shrimp onto 16 skewers', 'grill , covered , over medium-hot coals 2 minutes on each side or until shrimp turn pink', 'serve immediately over rice']", "11", "152.9", "3.0", "3.0", "53.0", "55.0", "1.0", "1.0", "None", "lunch", "3-steps-or-less,easy", "shrimp", "None", "dinner-party", "shrimp", "None", "23.65");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("490278", "spicy korean pork barbecue", "(daeji bulgogi) - from cooking light. a distinctive asian flavor -- the caramel notes of brown sugar balance the heat of the sambal oelek and crushed red pepper). prep time does not include refrigeration and marinating times.", "20", "12", "['wrap pork in plastic wrap', 'freeze 1 1 / 2 hours or until firm', 'remove plastic wrap', 'cut pork diagonally across grain into 1 / 16 inch thick slices', 'combine pork , sugar and next 6 ingredients in a large zip-top plastic bag', 'seal and marinate in refrigerator 1 hour , turning bag occasionally', 'prepare grill', 'place a wire grilling basket on grill rack', 'remove pork from bag', 'discard marinade', 'place pork on grilling basket-coated with cooking spray', 'grill 5 minutes or until desired degree of doneness , turning frequently']", "9", "180.6", "7.0", "27.0", "13.0", "48.0", "7.0", "2.0", "None", "lunch", "3-steps-or-less,easy", "pork", "None", "dinner-party", "pork", "None", "14.19");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("366037", "lemony baked tofu", "yummy as a snack, sandwich filling, or topping for salads! it's got lots of flavor and holds it's shape well. a choice is given for two very different but equally inviting marinades. be sure not to use an aluminum baking pan! use the smallest baking pan you have that will hold the tofu in a single layer. an 8' square pan and a 7x9' pan both work fine. adapted from moosewood restaurant new classics cookbook.", "75", "11", "['preheat the oven to 400f', 'cut the tofu cake horizontally into four slices and set aside', 'choose a marinade and whisk together all ingredients until smooth', 'pour half in a small nonreactive baking pan', 'place the tofu slices in the pan and pour on the remaining marinade', 'bake 45-60 minutes , turning the tofu once after about 30 minutes', 'the baked tofu should be browned , bubbling , and curling up at the edges', 'remove from the oven and with a metal spatula , move the tofu slices to a serving platter', 'serve hot , warm , at room temperature , or chilled', 'the tofu will keep in an airtight contaiiner or plastic bag in the fridge for up to 5 days', 'enjoy !']", "10", "555.4", "77.0", "14.0", "85.0", "45.0", "38.0", "3.0", "None", "lunch", "None", "beans", "None", "None", "beans,soy-tofu,vegetables", "None", "30.13");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("70337", "potato parmesan and anchovy focaccia", "awww...c'mon, live a little - the anchovies won't kill you. promise.", "65", "12", "['proof yeast in sugar and water for 5 minutes , until foamy', 'add flour , mashed potatoes , salt and 2 tblsps olive oil and combine dough well', 'knead for 2 minutes , form into a ball , transfer to an oiled bowl and turn to coat', 'let rise , covered with plastic wrap in a warm place , for 1 hours , or until double in size', 'press dough into 15 x 10 inch oiled jelly-roll pan', 'cover loosely , put in a warm place and let rise 1 more hour', 'preheat oven to 400 degrees f', 'place rack in bottom third', 'stir together garlic , rosemary , anchovy paste and remaining 4 tblsps oil', 'arrange sliced potatoes on dough , overlapping', 'brush with oil mixture', 'sprinkle with parmesan and salt and bake in bottom third of oven for 40-50 minutes , or until golden-brown']", "13", "3568.3", "149.0", "61.0", "561.0", "175.0", "88.0", "194.0", "None", "lunch", "None", "potatoes", "None", "None", "cheese,potatoes,vegetables,yeast", "None", "24.57");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("187433", "greek yoghurt and honey", "i have this most days at school because it is the only yoghurt they provide, but i also have it everyday at home because i love it so much. i like to have it when i have a sore throat as well because it is the best medicine!!!", "1", "2", "['serve yoghurt in bowl with honey on top', 'very sticky but satisfying']", "2", "63.8", "0.0", "69.0", "0.0", "0.0", "0.0", "5.0", "None", "breakfast", "3-steps-or-less,5-ingredients-or-less,beginner-cook,easy,kid-friendly", "None", "None", "None", "None", "summer", "25.33");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("72724", "very good for your health and mine vegetarian lemon pepper soup", "mmmm, nothing like a good bowl of veg soup! this is the famous indian chef sanjeev kapoor's recipe. like him, i believe that good soup is easy to make if you take care to choose the freshest ingredients, especially those that are in season. beautifully garnished, any soup would look as pretty as a bride with a veil over her charming face;-) add a few croutons in the centre of your soup for a contrasting texture and a complementary flavour! scoop up your soup from a special cook with a wonderous look, looking up from his favourite cookbook(ah i'm in a scooby dooby good mood n playing with words;-) )", "55", "13", "['firstly , prepare vegetable stock', 'for this , dissolve 2 cubes of maggi vegetable stock with onions in 4-5 cups of water and boil until the cubes are completely dissolved', 'your veg', 'stock is ready', 'now keep this aside and proceed', 'heat oil in a saucepan', 'add carrot , cabbage and mushrooms and stir well', 'add capsicum , tomato and salt', 'stir-cook for 10-15 minutes till the veggies are tender', 'then , add the stock and let it come to a boil', 'lower heat , stir in the crushed peppercorns and remove from flame', 'add bean sprouts and lemon juice', 'stir well and serve hot on a cold windy winter night']", "11", "99.9", "4.0", "42.0", "1.0", "9.0", "2.0", "5.0", "stir-fry", "lunch", "beginner-cook,easy", "beans", "stove-top", "dinner-party", "beans,vegetables", "winter", "13.56");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("437919", "london broil bulgogi", "cheap, but yummy bulgogi!!!!!", "17", "15", "['it is very important to freeze the meat first , this aids you in cutting the meat into thin slices', 'after freezing , slightly defrost - make sure the meat is still frozen , but workable', 'cut the meat into thin slices , as thin as possible', 'to prepare marinate mix all ingredients in a medium glass bowl', 'place sliced london broil in marinate and press meat into bowl', 'top with sliced onion', 'marinate in refrigerator for 16-24 hours', 'meat will turn dark brown', 'it is a must to marinate for 16-24 hours if using london broil ! if you do not want to wait use a better cut of beef', 'heat 2 tdsp of oil in a cast iron skillet or wok', 'grab most of the onions out of bowl', 'squeeze marinate out', 'add onions to skillet and cook for 3 to 5 minutes', 'drain marinate out of beef and add to onions', 'cook on high for an additional 3 to 4 minutes flipping often']", "14", "433.1", "23.0", "112.0", "104.0", "71.0", "25.0", "11.0", "None", "lunch", "None", "None", "None", "None", "beef", "None", "32.27");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("346586", "yemista greek stuffed peppers", "these peppers stuffed with a seasoned ground pork and rice mix are a greek favourite. you can also use the pork mixture to stuff zucchini and baby eggplants. serve with a green salad and crusty bread.", "55", "12", "['neatly cut the tops off the peppers and set the tops aside', 'remove the membrane and seeds from the peppers and discard', 'heat 4 tablespoons of the oil in a heavy-based saucepan and fry the onion until it begins to colour', 'stir in the rice and ground pork', 'add the herbs , cinnamon , salt and freshly ground black pepper to taste and the tomatoes', 'stir until the rice becomes transparent and the meat loses its redness', 'preheat the oven to 350f', 'use the mixture to loosely fill the empty peppers', 'arrange the filled peppers snugly in a casserole dish', 'put the reserved pepper tops back on the peppers , like lids', 'combine the lemon juice , water and remaining oil and pour into the dish around the peppers', 'bake for about 30 minutes until the peppers are tender and the filling is cooked and hot']", "12", "474.4", "43.0", "56.0", "1.0", "23.0", "28.0", "16.0", "None", "lunch", "easy,kid-friendly", "pork", "None", "dinner-party", "pork,vegetables", "None", "33.61");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("50463", "wild crazy rice", "this recipe is good with grilled chicken or steak. i got this recipe from a news web site here in town and thought it sounded great so i thought i would share it.", "70", "6", "['preheat oven to 350 degrees', 'in a medium-size baking dish , combine rice and seasonings , soups , water chestnuts and mushrooms', 'using one of the opened soup cans , add one additional can of water to the mixture', 'stir until combined', 'cook uncovered for approximately 1 hour or until rice is tender', 'serve hot']", "5", "76.5", "1.0", "14.0", "42.0", "14.0", "0.0", "3.0", "None", "lunch", "5-ingredients-or-less,easy,one-dish-meal", "None", "oven", "dinner-party,valentines-day", "rice,vegetables", "fall,spring,summer,winter", "10.03");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("448296", "zaru soba chilled japanese noodles", "this is a dish i became addicted to in thailand, of all places... i'd never seen it here in australia, so i learned to make it myself. it's extremely refreshing in hot weather. one way of serving the noodles is to place them over a dish of ice to keep them cool, but that's not necessary if you prefer not to. if you can't find dashi, which is a type of stock prepared with bonito flakes and seaweed, i have found that shop bought instant miso soup can do at a pinch. you might need to fiddle with the proportions of ingredients for the sauce to suit your taste. some people like to add a little bit of sugar to the sauce as well.", "10", "15", "['combine dashi , shoyu , mirin and vinegar in a pan and bring to a simmer', 'remove from heat and store in refrigerator to chill', 'bring litre of water to boil', 'add soba noodles', 'cook for 4 mins', 'drain noodles using a colander', 'rinse with cold water and wash noodles , making sure all the starch is rinsed off', 'cut nori sheet in four', 'take one quarter and slice thinly', 'place slices of nori on noodles', 'slice spring onion and place on a small dish', 'put wasabi paste on another small dish', 'serve noodles on a bamboo mat traditionally , or just use a plate , with the dipping sauce and condiments in separate bowls', 'combine desired condiments into the sauce', 'dip noodles in sauce and eat !']", "8", "350.5", "1.0", "6.0", "112.0", "31.0", "0.0", "25.0", "None", "lunch", "easy", "None", "None", "None", "None", "None", "26.21");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("474224", "vegetarian japanese curry", "curry was introduced to japan by the first japanese sent overseas to learn the western ways. of course, they have taken it and made it their own. recipe from vegetarian times, march 2012, p.66", "70", "9", "['heat oil in large pot over medium heat', 'saute onion 7 to 9 minutes , or until onion starts to brown', 'add carrots and saute 5 minutes', 'stir in curry powder and garlic and cook 1 minutes , or until fragrant', 'add broth , potatoes , apple , ketchup , worcestershire sauce and miso', 'bring to a simmer', 'reduce heat to medium-low and cook 30 minutes , or until the potatoes are tender', 'add edamame and simmer 5 minutes more', 'serve over sticky rice']", "13", "357.8", "5.0", "30.0", "8.0", "13.0", "2.0", "25.0", "None", "lunch", "3-steps-or-less,beginner-cook,easy", "potatoes", "None", "None", "potatoes,rice,vegetables", "None", "15.3");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("290853", "amazing watermelon greek salad with feta", "this refreshing salad will surprise you and your guests with how well the flavours work together. at first, it might seem strange, but one taste and you will be hooked. the cool, crunchy watermelon is the perfect base for the pungent, greek flavours - it's a perfect balance of savoury and sweet. a similar salad was served to me at a cafe a few years back. i had always remembered it and finally decided to try to recreate it. this is about as close to the original i could get. :) ***note*** feel free to experiment with flavours. try adding in some fresh basil or fresh mint in place of the balsamic vinegar. or leave out the vinegar altogether.", "10", "7", "['in a small bowl , cover the onion slices with the lime juice', 'the acid in the lime juice will help to mellow the onion a bit', 'in a large salad bowl , combine the watermelon , feta , olives , and onions set aside', 'wisk together the reserved lime juice , olive oil and as much balsamic vinegar to suit your taste and preferences', 'keep in mind there is quite a bit of flavour in the salad from the feta and olives so just 1 or 2 tbsp usually does the trick', 'pour vinegrette over melon mixture and toss', 'garnish with freshly chopped parsley']", "8", "157.0", "15.0", "44.0", "12.0", "7.0", "18.0", "5.0", "None", "lunch", "easy", "salads", "None", "None", "cheese,fruit", "None", "33.7");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("21278", "tortellini salad with garden vegetables", "None", "40", "5", "['cook tortellini according to package to package directions until just tender', 'drain , then rinse under cold running water', 'drain again and transfer to large bowl', 'add remaining ingredients and toss well to combine', 'serve at room temperature or chilled']", "6", "519.1", "32.0", "30.0", "48.0", "36.0", "30.0", "22.0", "None", "lunch", "beginner-cook,easy", "salads", "oven,refrigerator,stove-top", "dinner-party,easter,new-years,valentines-day", "vegetables", "fall,spring,summer,winter", "16.28");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("105160", "greek bbq baby octopus", "the hard long work pays off, i got it from a chef mate and he got it off his grandma. he tells a story of his grandfathers aussie neighbours couldn't believe he would cook up very cheap fish bait (baby octopus) for his family. that was untill he tried it, and my family has been eating it ever since.", "55", "15", "['place the octopus and garlic in a pot with water and bring to boil', 'add baking soda and simmer for 40 minutes', 'to prepare the marinade', 'in a bowl add 300ml of olive oil , juice of one lemon and lime , salt', 'and pepper', 'when the octopus is tender rinse it in cold water', 'place in the', 'marinade and leave overnight', 'the following day , grill on a hot plate until lightly coloured', 'flamb cognac in a fry pan , add the butter and slowly melt , whisking', 'constantly', 'add a touch of cream and worcestershire sauce and whisk', 'till they are all combined', 'finish with shallots', 'serve on a bed of rice with the cognac sauce poured all over']", "15", "750.2", "104.0", "3.0", "33.0", "52.0", "103.0", "3.0", "None", "lunch", "None", "None", "None", "dinner-party", "None", "None", "19.57");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("327691", "william sonoma s greek yogurt with pears and honey", "greek yogurt (yiaourti) is thicker and creamier than the variety commonly found in u.s. markets. in greece, yogurt is a much-used ingredient in many recipes and a favorite treat spooned from the bowl with honey or nuts. traditionally made with sheeps milk, greek yogurt is easy to prepare at home using cow's milk. the yogurt is always strained to remove excess moisture, producing a luscious, creamy texture.", "1455", "13", "['in a large saucepan over medium heat , warm the milk , stirring occasionally , until it registers 160f on an instant-read thermometer', 'transfer the milk to a large bowl with a pouring spout and let cool to lukewarm', 'pour 1 / 4 cup of the milk into a small bowl and whisk in the yogurt starter', 'whisk the starter mixture into the large bowl of milk , making sure to incorporate all of the starter', 'pour the milk into yogurt jars , dividing evenly , and place in a yogurt maker', 'process for 13 hours according to the manufacturers instructions', 'line a fine-mesh sieve with coffee filters or 4 layers of cheesecloth and place over a bowl', 'pour the yogurt into the sieve and cover with plastic wrap', 'refrigerate until the yogurt is thick , at least 8 hours', 'transfer the drained yogurt to a clean bowl and stir until smooth', 'if the yogurt is too thick , like cream cheese , whisk a little of the strained liquid back into the yogurt', 'to serve , divide the yogurt among 4 shallow bowls', 'top with the pears and nuts and drizzle with the honey']", "5", "386.4", "25.0", "115.0", "9.0", "24.0", "39.0", "17.0", "None", "breakfast,lunch", "5-ingredients-or-less,easy", "None", "None", "None", "None", "None", "19.11");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("116415", "savory potato skins worth waiting for", "these potato skins are different from the usual potato skins you get in a restaurant  no tomato sauce or cheese. my dh asked for more of these, and he doesnt even like potatoes! if you bake the potatoes in the microwave, you might try crisping up the skins by rubbing them with butter and salt, and putting them, empty, in the oven for a few minutes or briefly frying them. cook time is marinating time, and it is assumed that most of the prep work is done sometime during that time.", "135", "12", "['marinate chicken in wine for 2 hours minimum', 'bake potatoes', 'fry chicken in a little oil in a frying pan', 'add marinade to the chicken in the pan and cook it down to make a kind of glaze on the chicken', 'cut potatoes in half', 'scoop out the insides , leaving in shell', 'reserve the insides of the potato for another use', 'place chicken , eggs , onion , coriander , dehydrated milk , salt and pepper in a food processor and pulse until the ingredients are ground finely', 'scrape down the sides of the bowl a couple of times so that everything gets ground evenly', 'adjust seasoning at this time', 'scoop chicken mixture into potato skins and dust with remaining coriander', 'serve warm or cold']", "9", "340.6", "16.0", "16.0", "3.0", "30.0", "13.0", "12.0", "None", "lunch", "None", "potatoes", "food-processor-blender", "None", "chicken,eggs,potatoes,vegetables", "None", "32.16");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("18268", "garlicky tortellini spinach and tomato soup", "i first found this in fine cooking magazine and adapted it for my family. it's one of the few times i will be ready-made tortellini.", "30", "10", "['in a large saucepan , melt the butter over medium heat', 'add the garlic and onion and saute until fragrant , 2-3 minutes', 'add the chicken broth and water', 'bring to a boil', 'add the tortellini and cook halfway , about 5 minutes for frozen pasta , less if using fresh', 'add the tomatoes and their liquid , reduce the heat to a simmer and cook just until the pasta is tender , about 4 minutes', 'stir in the spinach and basil and cook until wilted , 1-2 minutes', 'meanwhile , whisk together the egg , parmesan cheese , salt and pepper', 'drizzle into the soup and cook , stirring about 3 minutes more', 'remove from heat and serve']", "12", "252.3", "16.0", "15.0", "19.0", "24.0", "28.0", "9.0", "None", "lunch", "None", "None", "stove-top", "dinner-party", "vegetables", "None", "35.98");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("288858", "garlic roast beef pinwheels", "a recipe i saw recently!! very good and easy to make. made these for a baby shower i hosted and they were a big hit!!!", "37", "16", "['preheat oven to 375f heat olive oil in medium nonstick skillet over medium heat', 'add onion and garlic', 'cook and stir 3 to 4 minutes or until onion is tender', 'remove from heat', 'add beef , basil , marjoram and pepper', 'toss to mix well', 'set aside', 'unroll dough and separate into 2 long rectangles', 'press each into 12x4-inch rectangle , firmly pressing perforations to seal', 'spread each half with half of beef mixture', 'starting at one short side , firmly roll both pieces , crimping seam to seal', 'with serrated knife and sawing motion , gently cut each roll into 8 pieces', 'place cut side down on baking sheet sprayed with nonstick cooking spray', 'gently reshape pinwheels after placing on cookie sheet , if necessary', 'spray pinwheels lightly with nonstick cooking spray and sprinkle with sesame seeds before baking , if desired', 'bake 13 to 16 minutes or until edges are golden brown']", "9", "213.8", "13.0", "6.0", "48.0", "32.0", "13.0", "5.0", "None", "lunch", "3-steps-or-less,beginner-cook,easy", "sandwiches", "None", "picnic", "beef", "None", "39.78");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("10770", "fassolia gigantes plaki giant beans baked in the oven", "None", "90", "14", "['pick the beans clean from stones and grit and soak them in cold water overnight', 'drain and rinse them in a colander , put into a large saucepan or a pressure cooker , cover with plenty of cold water and boil them until they are cooked but still a little hard', 'this type of bean cooks quickly , so be aware until you get used to them', 'they take hardly 2 minutes in a pressure cooker and about 30-40 minutes otherwise', 'if they get overcooked , they get mushy and soggy and they do not taste at their best', 'this first step then is also the most important in this recipe', 'drain the beans through a colander and keep aside', 'in the same saucepan , heat the olive oil and fry the onions , the garlic and the dried herbs , until they start turning pale golden', 'add the tomatoes with all the juice from the cans or , if fresh tomatoes are used , add 150 ml water', 'then add the tomato puree and sugar , break up the mixture with a fork , season with salt and pepper , cover and cook for 30 minutes or until the sauce thickens', 'mix the parsley into the tomato sauce , add the beans and mix well', 'spread the beans in an oven dish', 'sprinkle a little oregano and black pepper on the top and cook in a pre-heated oven at 350 grades f / 80 grades c , for 30 minutes , without stirring , until they look slightly crisp on top and quite dry', 'alternatively , add the beans to the tomato sauce , mix well , cover and simmer for 10-15 minutes']", "11", "390.7", "54.0", "43.0", "0.0", "6.0", "24.0", "6.0", "None", "lunch", "beginner-cook,easy", "beans", "oven", "dinner-party,picnic", "beans,vegetables", "None", "28.04");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("13678", "tempeh sukiyaki with broccoli cauliflower", "a meal in one pan and very good. this recipe is from 'the tempeh cook book'. tempeh is a soy product sometimes combined with wild rice or other grains. there are several different kinds. tempeh has more texture than tofu, you can actually see the soy beans. for vegetarians sub vegetarian worcestershire sauce for regular and for vegan leave out the honey.", "40", "11", "['put the steamed tempeh in the marinade and stir once in a while to coat all of the tempeh', 'bring the vegetable stock to a boil and add carrots , broccoli& cauliflower , boil for 1 minute only , remove veggies immediately , reserve stock for sauce', 'drain tempeh , adding any left over marinade to the stock', 'heat wok or skillet add 2 tbsp oil', 'fry tempeh uintil browned remove to a bowl', 'heat pan add remaining 2 tbsp oil', 'stir fry onions& peppers for a few minutes add celery cook 2 minutes', 'pour liquid stock into the pan , bring to a simmer , add 2 tbs tamari', 'add tempeh , carrots , broccoli & cauliflower stir until heated', 'do not over cook the vegetables should still be crisp', 'serve with brown rice']", "15", "204.8", "20.0", "17.0", "38.0", "19.0", "10.0", "4.0", "stir-fry", "lunch", "one-dish-meal", "beans", "None", "dinner-party", "beans,soy-tofu,tempeh,vegetables", "None", "12.74");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("338874", "waffles", "from mom's spiral recipe notebook", "30", "5", "['sift dry ingredients', 'beat eggs , milk , & melted lard', 'add wet mixture to dry ingredients', 'if sweet waffles are desired , add the sugar', 'pour onto a hot waffle iron']", "7", "109.8", "7.0", "0.0", "6.0", "6.0", "10.0", "4.0", "None", "breakfast", "None", "pancakes-and-waffles", "stove-top", "None", "None", "None", "23.34");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("68466", "fat free egg rolls", "this was a surprise to me at how good the results were--egg rolls with no fat, no deep frying---wow! they are fantastic, and fast and easy to make. don't let the list of ingredients scare you, you can use more or less of one thing, or eliminate a vegetable, or replace one, with no problems. a really great recipe, very low calorie too!", "70", "14", "['make sure all meat is cooked , and everything mentioned as'chopped' above is chopped', 'i use the food processor', 'mix everything but the egg roll wrappers in a very large bowl', 'use your hands to be sure to get a good mix', 'place an eggroll wrapper in front of you , with a point up , as in a diamond', 'put about a third of a cup of filling horozontally across the middle , bring down the top point , tuck under the filling , and roll , folding in the two side points as you go', 'seal the bottom point with some water before you roll completely up', 'if in doubt , check the back of the eggroll wrapper package for pictures', 'spray the biggest cookie sheet you have with pam , put eggrolls on it , make sure they aren't touching', 'spray the tops of the eggrolls with pam', 'bake for 30-40 minutes', 'if the tops seem cooked and crispy , flip them over for the last ten minutes of baking', 'eggrolls are done when they are crispy-crunchy and browned', 'check oven to be sure of progress and time , as the cooking time seems to vary with how wet , and how much filling you use']", "14", "256.0", "13.0", "5.0", "17.0", "22.0", "15.0", "10.0", "None", "lunch", "beginner-cook,easy", "shrimp", "oven", "None", "chicken,shrimp,vegetables", "None", "28.09");
