INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("12922", "sesame wafers", "these have a nice crisp snap (do not freeze)", "18", "11", "['sift flour and baking powder', 'cream butter , sugar , egg and vanilla', 'add sifted flour and baking powder', 'mix to combine', 'stir in seeds', 'line cookie sheet with parchment paper or aluminum foil', 'spray with veggie oil', 'drop by small teaspoonfuls , leaving room for spreading', 'bake at 350 for 6-8 minutes or until lightly browned', 'cool on sheet , then remove to rack to cool completely', 'store in an airtight container']", "7", "510.9", "48.0", "142.0", "6.0", "15.0", "60.0", "17.0", "None", "lunch", "easy,kid-friendly", "None", "oven", "None", "grains", "None", "23.69");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("117455", "samosas", "this comes from the time life series of international cookbooks and has been a family favourite in our house for at least 10 years. it's best to set aside a morning to make these and haul someone in the kitchen to help put them together. although they are not difficult to make, it does go faster if you set up an 'assembly line'. the seasoning given here is what i would consider a minimum but you may want to add more to your taste, especially when it comes to the hot pepper. you could also do half potatoes and half carrots for the filling if you like. the amount this recipe makes is also a guess based on our samosas, which are bigger than bite sized but not as big as you get in some indian shops. if you don't have a deep fryer, you could use a wok instead. if you are really set on doing a healthier version, you could brush these with oil and bake in an oven until browned on both sides, however the texture of the dough is not as good as if you fry them. if you choose the baking option, you may also have to brush them with oil a few times and flip at least once to get them reasonably browned all over. frying is really the preferred method.", "135", "25", "['first put the potatoes on to boil until tender , then drain and cool', 'while the potatoes are cooking , make the pastry by mixing the flour and salt in a bowl', 'pour in the butter or ghee and rub together wtih the flour until it becomes slightly flaky', 'pour 3 / 4 cup water over the flour and pull the dough together with your hands', 'you can add up to 4 tbsp more water , one spoonful at a time , until everything comes together in a bowl', 'from here you can knead the dough by hand for about 10 minutes or throw it in a mixer with a dough attachment , going until the dough is smooth and elastic', 'when the dough is done , place it in a bowl , rub with oil and cover until you are ready to fill the samosas', 'next , start making the filling by cutting your cooked potatoes into cubes', 'heat the oil in a frying pan and throw in the mustard seeds', 'cook until they start to crackle and burst and when that happens immediately add your chopped onion and ginger', 'cook for a few minutes until the onion softens and then add the fennel , cumin , turmeric , potatoes , peas , salt and water', 'stir well and then reduce the heat to low and let cook for about 5 minutes', 'sprinkle over the garam masala and hot pepper and stir again', 'taste for seasoning and then take off the heat', 'now it is time to start filling your samosas', 'grab a piece of dough by pinching off a piece and rolling it thinly and into a circle', 'you can make the samosas any size you like', 'with a knife , cut the circle in half , moisten the straight edge with a finger dipped in water and then shape it into a cone , pressing the straight edges together to form a seal', 'you should now have a little ice cream cone shaped piece of dough which you can spoon filling into', 'fill and then moisten the top edges and press closed', 'set on a plate and cover with foil or plastic wrap until you are ready to cook', 'to deep fry the samosas , pour 3 cups of vegetable oil into a 12 inch wok and heat until a piece of dough thrown into the oil bubbles', 'if you have a deep fryer , you want a temperature around 375f', 'brown on both sides and drain on paper towel', 'finally , you can enjoy a samosa !']", "17", "135.9", "7.0", "4.0", "9.0", "5.0", "7.0", "6.0", "None", "lunch", "None", "potatoes", "None", "None", "potatoes,vegetables", "None", "23.75");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("471972", "mexican grilled cheese sandwich quesadilla", "ok, a mexican grilled cheese is a quesadilla but this is a good one! this recipe is from a canadian cheese magazine. enjoy!", "23", "3", "['evenly distribute toppings on 4 tortillas', 'top with remaining four tortillas', 'heat skillet over medium heat and cook tortilla sandwiches approximately 4 minutes on each side or until cheese is melted and tortillas are browned']", "6", "60.5", "0.0", "1.0", "0.0", "8.0", "0.0", "3.0", "None", "lunch", "3-steps-or-less,easy", "beans,sandwiches", "None", "None", "beans,cheese,vegetables", "None", "32.87");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("381266", "grilled chicken with greek flavors", "only five ingredients makes this one come together quickly and flavorfully, too! from one of my favorite magazines (country living, june 09), this recipe is just right for a summer meal with the family or to serve guests. prep time includes marinating time. cook time includes only the time on the grill.", "80", "12", "['marinate breast meat with other marinade ingredients for at least one hour', 'remove chicken from marinade', 'cook on a grill over medium-high heat , turning once until cooked through , about 10-12 minutes', 'keep warm', 'while the chicken is cooking , half the tomatoes and toss with reserved vinaigrette and salt in a medium bowl', 'lower grill heat to medium', 'spread tomatoes in a foil pan', 'sear tomatoes cut sides down , about 2 minutes', 'return tomatoes to bowl , toss with oregano', 'slice chicken and transfer to serving platter', 'spoon tomatoes alongside chicken', 'sprinkle feta cheese and more oregano over tomatoes']", "6", "300.7", "24.0", "13.0", "27.0", "65.0", "27.0", "1.0", "grilling", "lunch", "easy,one-dish-meal", "None", "None", "camping", "chicken,vegetables", "summer", "13.35");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("292166", "buffalo chicken sandwiches", "i found this recipe online and made a few changes to suit what i had available. if you have leftover grilled chicken, use it instead of the pre-cooked chicken strips. i like to use homemade blue cheese dressing. these are very sloppy sandwiches so be sure to have lots of napkins or a wet washcloth handy! enjoy!", "25", "7", "['in a medium saucepan , melt the butter over medium-low to low heat', 'add the garlic and cook for 30 seconds', 'add the hot sauce , barbecue sauce , and chili powder and stir to combine', 'add the chicken and cook the mixture uncovered over medium-low heat for five minutes , or until heated through', 'spoon chicken onto bun bottoms', 'top with lettuce and blue cheese dressing', 'top with bun tops']", "9", "413.1", "42.0", "15.0", "44.0", "13.0", "62.0", "11.0", "None", "lunch", "3-steps-or-less,easy", "sandwiches", "oven,stove-top", "None", "cheese,chicken", "None", "27.9");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("428564", "five happiness fried noodles", "from food stamps/snap-ed recipe finder. per recipe: $ 3.76 per serving: $ 0.75 i added a dash pf sesame oil. you could add leftover chicken or ham, baby corn, zucchini...", "27", "9", "['1', 'in a large pot on high heat , bring 10 cups of water to boil', 'cook noodles in boiling water until tender or al-dente , approximately 5 - 7 minutes , drain and set aside', 'heat a nonstick wok or pan over high heat', 'add 1 / 2 tablespoon oil , swirling to coat the pan , add noodles and pan-fry until noodles are golden , about 7 to 10 minutes', 'remove noodles from wok', 'add remaining 1 / 2 tablespoon oil to wok , add vegetables , and stir-fry until just cooked , about 5 to 7 minutes', 'return noodles to the wok , add salt , sugar and soy sauce', 'stir to combine and serve']", "11", "413.5", "10.0", "27.0", "20.0", "32.0", "7.0", "24.0", "stir-fry", "lunch", "None", "None", "stove-top", "None", "None", "None", "11.6");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("363866", "good karma schawarma", "from food network, guy's big bite, nomadic dishes. i use boneless, skinless chicken breasts. [sometimes i cheat and marinate the chicken as the recipe calls for but then just bake the chicken. i then make wraps with the chicken, tzatziki sauce, fresh spinach leaves, chopped tomato, thinly sliced onion and store bought flat bread.] the tzatziki sauce is very good. you can substitute 1 1/2 - 2 t. garlic powder if you do not have fresh garlic cloves. i also sometimes use fresh dill or 1/2 t. dried dill in place of the fresh mint leaves.", "915", "37", "['in a large bowl , combine yogurt with garlic , onion , vinegar , parsley , oregano , salt , cumin pepper , red chili flakes , and sage', 'add chicken and marinate in the refrigerator for 12 to 24 hours', 'preheat oven to 350 degrees f with oven rack in center', 'fit a 9 by 13-inch baking sheet with a cooling rack', 'remove chicken from marinade , taking off excess marinade', 'place meat randomly on top of each other on the aluminum foil tray , creating 1-inch of meat', 'add spinach , spreading evenly , and repeat with another 1-inch layer of meat', 'add a layer of red bell peppers , and finish by covering with another inch of meat', 'press layers firmly together', 'remove foil tray with chicken to the baking sheet', 'poke holes in bottom of foil to allow excess liquid to drain', 'bake in center of oven for 45 minutes until chicken is firm and holds together', 'cut corners of tray and push the sides of foil down , to expose meat to brown', 'continue to cook for another 30 minutes or until the meat registers an internal temperature of 165 degrees f', 'using an instant-read thermometer', 'let rest for 10 minutes', 'remove schawarma to a cutting board , discarding foil', 'slice into 1 / 2-inch thick pieces', 'serve sandwiched in flatbread and drizzled with tzatziki sauce', 'flatbread:', 'in a medium bowl , combine flour and salt', 'using a wooden spoon , slowly stir in water , until dough forms', 'lightly flour a clean board and knead dough until smooth , about 10 minutes', 'rest dough in bowl , covered with damp towel for 20 minutes', 'preheat a griddle over medium heat', 'divide dough into 8 equal balls', 'using a rolling pin , roll balls out on floured board to 7 inches in diameter , about 1 / 4-inch thick', 'place the rolled out dough onto griddle', 'flip when raised bumps appear on the surface of the dough , about 1 to 2 minutes', 'cook second side until it turns golden', 'do not cook too long or flatbread will become brittle', 'cover flatbread with a towel up to 20 minutes before serving', 'yield: 8 flatbread rounds', 'tzatziki sauce:', 'in a medium bowl , mix all ingredients together well', 'refrigerate at least 1 hour', 'yield: 2 1 / 2 cups']", "21", "485.0", "27.0", "17.0", "81.0", "105.0", "37.0", "9.0", "None", "lunch", "None", "sandwiches", "None", "None", "vegetables", "None", "10.99");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("59380", "indian mutton biryani", "this recipe is from last week's thursday magazine. it was posted by hoorbanu iqbal. i feel that if you really want to enjoy biryani, have it hot with your fingers and not with a spoon. mmmmm! my brother loves biryani. come friday and biryani is what he wants;)", "210", "30", "['first of all , cut potatoes into quarters', 'apply salt to them', 'deep fry', 'pour oil / ghee in a skillet', 'fry the onions until pink in colour and crisp , remove them and keep aside before adding the cinnamon , cloves and the pastes', 'add cinnamon , cloves , peppercorns , bay leaves and green cardamoms', 'add ginger-garlic pastes', 'fry for 3 minutes', 'add in the washed mutton pieces', 'saute until the mutton gives out its own juices', 'add the slit green chillies', 'add turmeric , chilli and cumin powders', 'mix well', 'saute for 5 minutes , stirring continuously', 'mix in fresh yogurt', 'simmer until the mutton is cooked', 'add salt', 'continue to cook until the oil separates from the meat', 'add finely chopped corriander and mint leaves', 'in a separate pot , parboil rice until the grains are half cooked', 'strain the rice', 'spread out the mutton mixture in a heavy bottom pan', 'sprinkle the rice on top', 'add fried potatoes', 'sprinkle saffron milk over this', 'sprinkle crisp fried onions on top', 'cover the platter with a tight fitting lid', 'cook on low flame for 20 minutes', 'serve with plain chilled yogurt or raita and pappadams', 'enjoy !']", "23", "651.1", "37.0", "20.0", "3.0", "55.0", "36.0", "26.0", "None", "lunch", "one-dish-meal", "None", "stove-top", "dinner-party,ramadan,valentines-day", "rice", "None", "20.6");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("107724", "chinese braised beef and noodles", "braised beef with egg noodles and chinese seasonings.", "20", "12", "['cook the noodles in salted boiling water just until tender', 'drain , and rinse under cold water', 'cut the beef into very thin slices', 'mix together the garlic , salt , ginger , and five-spice powder , and add the mixture to the beef', 'mix', 'heat the oil and saute the beef over high heat for 2 minutes', 'mix together the soy sauce and broth , and add it to the beef', 'bring to a boil', 'mix the cornstarch with the remaining 4 tsp water , stir into the beef and cook until smooth and thick', 'add noodles , mix well and heat thoroughly', 'add the green onions , and stir for another minute', 'serve immediately']", "12", "533.9", "37.0", "8.0", "112.0", "68.0", "37.0", "15.0", "stir-fry", "lunch", "beginner-cook,easy,one-dish-meal", "None", "stove-top", "dinner-party", "beef,vegetables", "summer", "31.37");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("169964", "mouth watering teriyaki burgers", "this twist for a burger will have them coming back for more! wish i could take credit for this recipe, however i got it out of a cookbook. my husband said it was the best burger he ever had!!!", "25", "6", "['mix first 6 ingredients in a mixing bowl', 'mix anywhere from 1 / 2 to all of sauce into ground beef and form patties to your desired size', 'fry or grill patties as you would for any burger you would make', 'serve on sandwich buns or rolls', 'you can also freeze patties to be served at a later time', 'use wax paper in between patties and store them in freezer bags']", "7", "341.5", "21.0", "102.0", "86.0", "53.0", "24.0", "9.0", "grilling", "lunch", "beginner-cook,easy,one-dish-meal", "burgers,sandwiches", "stove-top", "dinner-party", "beef", "summer", "28.67");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("176527", "perfect japanese rice", "basic onigiri (riceball) recipe. add any filling you want i particularly like egg scrambled with some soy sauce (dark) and toasted (brush with a little miso mixed with sake and grill) onigiri can be very hard to handmake. moulds are available but difficult to get in the uk without paying high prices, small jelly mould or egg cups work really well.", "45", "11", "['wash rice in a colander until water runs clear', 'drain well and then soak for 30 minutes', 'put rice and water in a sauce pan make sure it has a tight fitting lid and wrap a tea towel around the lid to keep the steam inside', 'heat until boiling then turn to low and steam for 15 mins finally turn the heat off and let stand for a further 15 minutes', 'do not lift the lid off at this time !', 'let the rice cool enough so it is warm but won't burn your hands when picked up', 'at this point if using fillings or flavorings add them to the rice', 'wet hands and add a little salt if you feel like it', 'take some rice and shape it into a circle , triangle , cat head whatever you want', 'it is quite difficult don't worry if yours are wonky !', 'add filling and or garnish at this stage and finish with a strip of nori either round the edge or as a 'napkin' at one end']", "3", "268.5", "0.0", "0.0", "0.0", "9.0", "0.0", "19.0", "None", "lunch", "None", "None", "None", "picnic", "rice,white-rice", "None", "27.54");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("50408", "oriental roasted stuffed chicken", "found this recipe in a cookbook, and made this for christmas instead of the usual roast turkey. just for a little oriental touch. looks good on the table, as good as restaurant made, or better! according to my christmas guest.", "65", "14", "['soak the mushroom slices in water till they soften', 'keep the soaking liquid aside', 'loosen skin of chicken', 'rub the marinade under the skin , in the cavity and over the skin', 'do keep a little bit of marinade aside for the gravy', 'tuck the sliced mushrooms under the skin of the chicken', 'heat oven to 200c', 'roast chicken in a large roasting tin , loosely covered with foil for 30 minutes', 'turn heat down to 150c , add shallots and garlic , and roast uncovered for 20 minutes', 'pierce with a skewer , if the juice runs clear , the chicken is ready', 'to make the gravy: pour out excess oil from the roasting tin , add a cup or so of mushroom water , and leftover marinade to the pan', 'heat through on top of the stove', 'taste to adjust seasoning', 'place the chicken on a pretty plate , and garnish with the baby pak choy']", "9", "664.3", "53.0", "54.0", "32.0", "93.0", "49.0", "11.0", "None", "lunch", "beginner-cook,easy,one-dish-meal", "None", "oven", "dinner-party,easter,new-years,picnic,valentines-day", "chicken,vegetables", "fall,spring,winter", "37.51");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("104624", "black bean and corn wontons with 3 cheese sweet pepper sauce", "something a bit different. for healthier version use no-salt-added black beans, no-salt-added salsa, nonfat cottage cheese, light beer, and fat-free mozzarella.", "35", "16", "['mash together beans and salsa', 'stir in corn , onions and cumin', 'place bean mixture evenly in centers of 10 wonton skins', 'brush edges of wontons with water , top with remaining skins , and seal by press edges firmly together , pushing out air', 'trim edges with a fluted pastry wheel , and cover with a damp towel until ready to cook', 'combine red pepper , cottage cheese , beer , and garlic in an electric blender , cover and process until smooth , stopping once to scrape down the sides', 'transfer pepper mixture to a saucepan , and add the mozzarella and parmesan', 'cook until cheese is melted , stirring constantly', 'remove from heat and keep warm', 'bring water to a boil in a dutch oven', 'add wontons , and return to a boil', 'reduce heat and simmer 4-5 minutes or until wontons are tender', 'remove wontons with a slotted spoon and drain well', 'coat a baking sheet with cooking spray , and place wontons on it', 'broil 5 1 / 2' from heat with door partially open for 3-4 minutes or until lighlty browned', 'divide wontons and cheese mixture between two plates , and garnish with cilantro if desired']", "15", "464.1", "12.0", "17.0", "46.0", "47.0", "17.0", "24.0", "broil", "lunch", "easy,one-dish-meal", "beans", "food-processor-blender,oven,stove-top", "dinner-party", "beans,cheese,vegetables", "fall,spring,winter", "33.46");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("12744", "miso shiru soup", "a very traditional japanese soup", "35", "16", "['----------make the dashi---------', 'bring water to a boil', 'wash kelp under cold water tap', 'add to water when it comes to a boil', 'stir and let boil 3 minutes', 'remove kelp from the water and add the bonito', 'allow to come to boil and remove from heat immediately', 'let it settle for a few minutes', 'strain off the bonito and set aside for whenever you need the dashi', '---------make the miso shiru-----------', 'bring the dashi to a boil', 'mix a little hot liquid with the bean paste , stirring until smooth', 'pour mixture back into the saucepan , stir well', 'add tofu and return to boil', 'simmer for a few seconds only', 'ladle into bowls and garnish with green onion and a slice or two of mushroom']", "8", "40.6", "2.0", "4.0", "13.0", "7.0", "1.0", "1.0", "None", "lunch", "None", "None", "stove-top", "dinner-party", "vegetables", "None", "20.62");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("420747", "feta and pepper frittata", "this flavorful frittata is perfect for a light and easy family lunch. or serve with sauteed potatoes and green beans for a more substantial dinner. this frittata is also delicious served with roasted vine tomatoes.", "50", "6", "['heat the oil in a large , non-stick frying pan , add the peppers and cook over a medium heat for 15 mins until softened and lightly coloured', 'scatter over the feta', 'combine the eggs , cream and herbs , then season well', 'pour over the pepper mixture , reduce the heat and cook for 15-20 mins until just set', 'cook the top under a pre-heated broiler for 5 mins until just golden', 'cut into thick wedges and serve']", "8", "325.3", "39.0", "17.0", "27.0", "30.0", "66.0", "3.0", "None", "breakfast,lunch", "3-steps-or-less,easy,kid-friendly", "omelets-and-frittatas", "None", "None", "cheese,eggs,vegetables", "None", "32.38");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("449994", "lemon herb homemade ricotta roasted tomato crostini", "this is such a nice fresh appetizer and so easy to make. don't shy away because there are 3-4 steps; it is actually very very easy, and so worth making. the ricotta will last 5 days (approx) in the refrigerator, and very important is to make sure to use good fresh milk and cream. i like to top my crostini with the arugula then the ricotta and tomato, but feel free to skip the arugula. to me it is such a nice compliment to the creamy ricotta. these bite size appetizers and a bowl of soup or a salad can be dinner for me.", "30", "36", "['ricotta -- first step is to make your ricotta which - seriously makes itself', 'it is so easy', 'you will need a heavy bottomed pot or strainer or colander , cheese cloth and a big bowl', 'step 1: set up your colander over a large bowl', 'make sure there is plenty of room in the bowl for the liquid to drain', 'take 2 pieces of damp cheese cloth and line the colander', 'step 2: in a medium to large pot , bring the milk , cream and salt to a rolling boil on medium heat stirring often', 'once it is boiling add in the vinegar and immediately remove from the heat and stir', 'the vinegar will cause the milk and cream to separate and form curds', 'stir a couple of times , wait 2-3 minutes , and then pour it into the strainer or colander on top of the cheese cloth', 'you will see the whey or clear liquid separate from the milk curds and drain to the bottom of the bowl', 'let it rest at room temp for about 15-20 minutes to cool before you put it in the refrigerator', 'just make sure as it sets in the refrigerator that none of the liquid in the bowl is touching the bottom of the ricotta', 'make sure to cover the ricotta with saran wrap', 'personally , i like to let mine set over night , but at least 4 hours to cool down and set', 'tomatoes -- i like to have these chilled rather than hot , so i make these ahead of time too', 'couldn't be easier', 'oven at 425 , line a baking sheet with aluminum foil or parchment paper and add the tomatoes', 'drizzle with olive oil and season with salt and pepper', 'bake for approximately 10 minutes until they begin to soften , but not falling apart', 'let cool , transfer to a plate and cover', 'done !', 'herbed topping -- add the ricotta , lemon zest and juice , scallions , herbs , salt and pepper', 'mix well and set to the side', 'i prefer to make this right before i serve', 'baguettes -- some people bake them on a high temp in the oven , personally , i prefer to broil mine', 'they take only a minute or two and it is easy', 'i line a baking sheet with foil or parchment paper and add the baguette slices', 'drizzle with olive oil and broil the first side', 'once golden brown , flip and broil just another minute until brown', 'remove and rub the garlic on the side you drizzled with olive oil', 'that is the side you will add the toppings to', 'the garlic is optional , but i think it gives it a great taste', 'finish -- top each of the baguette with a little of the arugula , then a spoon of the ricotta and a tomato slice', 'enjoy !', 'prep time doesn't include the chilling of the ricotta']", "19", "1528.6", "111.0", "84.0", "83.0", "109.0", "217.0", "55.0", "None", "lunch", "easy", "None", "None", "dinner-party", "cheese,vegetables", "None", "29.44");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("24904", "portabella mushroom quesadillas", "a healthy choice for cheesy eating! if you wish to lower the fat further, you could use all fat-free cheese, but it doesn't melt as well.", "50", "18", "['preheat oven to 450 degrees', 'cut portabello mushrooms into slices about 1 / 4' thick', 'heavily spray large baking sheet with olive oil cooking spray', 'spread mushrooms in single layer on sheet', 'place garlic on there also', 'bake in 450 degree oven for about 30 minutes , or until roasted and tender', 'lightly spray baking sheet', 'place tortillas on baking sheet', 'remove skin from garlic', 'mash garlic on tortilla with fork , spread around', 'do the same with the other tortillas', 'chop mushroom into pieces and divide among tortillas', 'mix together cheeses in a bowl', 'put approximately 1 / 4 cup of cheese blend on each tortilla', 'divide oregano among tortillas , and add a pinch of cayenne , if desired', 'fold tortilla in half and spray top lightly with olive oil spray', 'bake in 450 degree oven for approximate 5-8 minutes , or until cheese is melted and top is lightly browned', 'cut each quesadilla into 3 pieces and serve']", "8", "216.1", "8.0", "7.0", "18.0", "18.0", "8.0", "10.0", "None", "lunch", "kid-friendly", "None", "oven", "None", "cheese,vegetables", "None", "14.2");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("501580", "lime chicken taco s", "this is a trim healty mama recipe from gwen's nest. i have ammended it to taste. use any chicken left over so for supper the next night i reheated the chicken and added white wine, cream, and romano cheese. i served it over pasta and it was phenomenal.", "30", "10", "['dice chicken into 1 chunks or make into strips', 'put chicken into medium saucepan over medium heat', 'cook , stirring occasionally until cooked through and no longer pink', 'marinate the chicken in 2 / 3s of the sauce mixture', 'add a splash of tequila', 'dump the marinade and added the last 1 / 3 of fresh marinade to the chicken as it was nearly done cooking through and add cilantro', 'sprinkle with cayene pepper', 'heat tortillas in the oven on 200 by removing them from plastic packaging and placing them in lightly damp tea towel or paper towels and wrapping in foil', 'prep other side dishes and toppings', 'when chicken is finished cooking , add toppings and serve']", "19", "278.0", "9.0", "7.0", "23.0", "97.0", "6.0", "1.0", "None", "lunch", "easy", "None", "None", "None", "vegetables", "None", "27.3");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("484976", "corn capsicum curry", "a flavorful corn curry which is simple, high on taste, and served as main course", "40", "9", "['soak corn kernels in warm water for 15 minutes and keep aside', 'heat oil in a pan and add cumin seeds and ginger garlic paste', 'now add finely chopped onions and saute it', 'add black pepper and tomato puree and let it simmer for 10 minutes', 'now add remaining tomatoes and chilly pwder , turmeric powder and coriander powder and mix it well', 'when the paste thickens , add chopped bell peppers', 'add little water if it turns very dry', 'now add corn kernels and salt and saute for 10 minute', 'serve with paratha or chapati or rice']", "11", "157.8", "1.0", "39.0", "1.0", "11.0", "1.0", "12.0", "None", "lunch", "3-steps-or-less,easy", "None", "None", "None", "vegetables", "None", "19.16");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("424925", "cypriot fried haloumy cheese saganaki haloumi", "with gluten free option. a very quick to prepare tasty meze. we enjoy this as a delicious breakfast item or it could always be a snack! i serve it with pita bread but a crusty bread would be very good. modified from the complete middle east cookbook by tess mallos, cyprus section.", "8", "3", "['cut cheese into 1 / 4 inch slices , dip in water and dredge in flour if desired', 'heat enough oil in a small frying pan to cover base , fry cheese until slightly golden on each side', 'squeeze some lemon on top and serve with rest of the lemon wedges and a crusty bread or some pita to dip into the lemon flavoured oil']", "5", "192.2", "21.0", "1.0", "22.0", "22.0", "43.0", "2.0", "None", "breakfast", "3-steps-or-less,5-ingredients-or-less,easy", "None", "None", "ramadan", "cheese", "None", "10.15");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("440166", "eggs baked in tomatoes with prosciutto basil", "adapted from delicious magazine, 8/08", "25", "10", "['preheat the oven to 350', 'remove the tomato tops with a sharp knife', 'scoop out the seeds with a teaspoon and discard , them place the tomatoes upside-down on paper towel for about 15 minutes to drain', 'smash the basil with a pinch of salt with a mortar and pestle until it becomes a paste , then add a little extra virgin olive oil to loosen it', 'season with black pepper and smear the oil inside the tomatoes', 'wrap a slice of prosciutto or pancetta around each tomato and secure with a toothpick', 'place the tomatoes on a greased baking tray or in an ovenproof fry pan and cook in the oven for 15 minutes until the tomatoes soften slightly and the prosciutto is beginning to crisp around the edges', 'remove the tomatoes from the oven and carefully break an egg into each one', 'bake for about 5 minutes or until the eggs are cooked but the yolks are still runny', 'scatter with extra basil and serve immediately with toast soldiers']", "5", "156.4", "11.0", "29.0", "5.0", "23.0", "12.0", "3.0", "None", "breakfast", "5-ingredients-or-less,easy", "None", "None", "None", "eggs,vegetables", "None", "24.6");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("213935", "meatball sub sandwich casserole", "winter supper at its best! comfort food with a tantalizing sauce & lovely meatballs. get the kids in on the act, rolling meatballs is fun and gooshy (okay, everyone, 'wash your hands first!'). thanks to taste of home's quick cooking 2000 year book for their inspiration.", "65", "19", "['preheat oven to 400', 'in a bowl , combine onions , crumbs & cheese', 'add beef and mix well', 'shape beef into 1-inch balls', 'place on rimmed cookie sheet', 'bake for 15-20 minutes', 'turn oven down to 350', 'meanwhile , arrange bread cubes in an ungreased 13x9 pan', 'combine cream cheese , mayo , seasonings , and s&p', 'spread over bread', 'sprinkle one cup mozzarella cheese over the cream-cheese mixture layer', 'combine sauce , water & garlic', 'add meatballs and gently mix', 'pour this over the cheese layer', 'sprinkle remaining mozzarella', 'bake at 350 for 30 minutes', 'options: use half cheddar and half mozzarella', 'try some sliced black olives', 'sprinkle some grated zucchini in for added nutritional value']", "13", "776.9", "70.0", "70.0", "70.0", "69.0", "100.0", "18.0", "None", "lunch", "easy,kid-friendly,one-dish-meal", "casseroles", "oven", "dinner-party", "beef", "winter", "14.12");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("97176", "artichokes and shells", "i came up with this one night to satisfy both my husband's wish for an olive oil-based pasta dish, and my craving for artichokes. we really enjoyed this hot and also the next day for lunch as a cold pasta salad. cooking times are approximate since i didn't keep track, but it is a fairly good guess. everything should only be cooked until heated through and the sauce is thickened.", "20", "9", "['cook pasta according to package instructions', 'drain and set aside', 'while pasta cooks , heat 1 tablespoon of olive oil in a large pan over medium heat', 'add garlic and onion and saute for about 1 minute', 'add olives , bell pepper , pepperoncini , and white wine and cook for about 3 minutes', 'add artichokes with their liquid and tomatoes , cook for another 3 minutes , or until liquid reduces', 'remove from heat', 'stir in pasta , herbs , salt , pepper , and 1 tablespoon of olive oil', 'top with feta cheese and serve']", "16", "388.9", "22.0", "25.0", "30.0", "25.0", "16.0", "17.0", "None", "lunch", "one-dish-meal", "None", "stove-top", "None", "vegetables", "None", "30.33");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("360634", "tofu and broccoli stir fry martha stewart", "this recipe is from martha stewart's 'everyday food'. this stir-fry is delicious over brown rice or asian noodles. to toast the cashews, spread them on a baking sheet and cook in a 350-degree oven for about 10 minutes. if you like a sweet-and-sour flavor, add 1 tablespoon of sugar to the sauce in step 4.", "40", "20", "['arrange tofu in a single layer on a baking sheet lined with several layers of paper towels', 'top with more paper towels and another baking sheet', 'weight with canned goods', 'let tofu drain , about 20 minutes', 'meanwhile , cook broccoli in a large pot of boiling salted water until bright green , 2 minutes', 'drain', 'set aside', 'heat oil in a large nonstick skillet over medium heat', 'add tofu', 'cook until golden brown , turning gently halfway through , 10 to 15 minutes', 'transfer to paper towels', 'meanwhile , make sauce: in a small bowl , whisk together soy sauce , vinegar , red-pepper flakes , garlic , cornstarch , and 3 / 4 cup water', 'set aside', 'add broccoli to skillet', 'cook over high heat , stirring often , until crisp-tender , 2 to 3 minutes', 'whisk reserved sauce to combine', 'pour into pan', 'return tofu to pan', 'stir to coat , about 1 minute more', 'serve topped with cashews']", "10", "308.8", "30.0", "18.0", "38.0", "34.0", "17.0", "7.0", "stir-fry", "lunch", "beginner-cook,easy,kid-friendly,one-dish-meal", "beans", "None", "None", "beans,fruit,soy-tofu,vegetables", "None", "18.98");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("58163", "rice paper wrapped fish", "a dish that takes no time to whip up, but good for a normal meal or finger food for a party you are organizing. double the recipe...it's worth it! from donna hay's dining cookbook", "25", "9", "['wash fish , pat dry on absorbent paper and cut into 12 pieces', 'place chillies , sesame oil , coriander , basil and cumin seeds in a spice grinder or mortar and pestle , and grind until they form a rough paste', 'spread paste over fish', 'brush rice papers with warm water and set aside for 4 minutes or until they are soft', 'place a piece of fish on each round , fold in sides and roll to enclose', 'heat a little oil in a frypan over medium heat', 'cook fish parcels for 2-3 minutes on each side , or until rice paper is golden and crispy , and fish is tender', 'drain on absorbent paper', 'sprinkle fish with sesame seeds , and serve with steamed asian greens drizzled with oyster sauce and steamed jasmine rice']", "9", "251.6", "8.0", "4.0", "6.0", "92.0", "4.0", "0.0", "deep-fry", "lunch", "beginner-cook,easy,kid-friendly,toddler-friendly", "None", "stove-top", "dinner-party,picnic,valentines-day,wedding", "None", "fall,spring,summer,winter", "19.48");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("24666", "sopa seca de tortilla", "a rich and creamy mexican side dish - full of peppers, onions, cheese and corn tortillas. it will surprise you. the name means ", "60", "16", "['cut tortillas in strips about 3 inches long and 1 / 2 inch wide', 'in medium skillet heat 1 / 4 inch of oil', 'fry tortilla strips , a few at a time , about 10 seconds or just until limp', 'remove with slotted spoon', 'drain on paper toweling', 'pour off all but about 2 tablespoons oil from skillet', 'add green pepper strips , onion and garlic', 'cook till onion is tender', 'sprinkle vegetables with salt', 'in 1-1 / 2 quart casserole , combine tortilla strips , vegetables , and half of the cheese', 'pour whipping cream over all', 'cover and bake in 350-degree oven for 20 minutes', 'uncover', 'stir gently', 'sprinkle with remaining cheese', 'bake uncovered about 10 minutes more or until heated through']", "8", "310.7", "33.0", "8.0", "21.0", "16.0", "64.0", "7.0", "None", "lunch", "easy", "casseroles", "oven", "dinner-party", "cheese,vegetables", "None", "38.05");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("427046", "feta stuffed avocados", "this is from 'can-am cooks', from the can-am world police and fire games. posted for zwt #6!", "10", "3", "['combine feta , red pepper , green onion , olives , oregano , olive oil and vinegar , stir well , and set aside', 'just before serving , cut the avocados in half lengthwise , remove the pit and discard', 'brush the surface of the pulp with lemon juice , fill the cavities with the stuffing mix and serve immediately !']", "9", "300.6", "42.0", "8.0", "14.0", "11.0", "34.0", "3.0", "None", "lunch", "3-steps-or-less,easy,no-cook", "None", "None", "dinner-party", "cheese,vegetables", "summer", "11.28");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("40018", "frankfurter vegetable stir fry", "hey we stir-fry everything else these days, so why not hot dogs!!", "30", "12", "['in 9 or 10' nonstick skillet , heat oil over medium heat', 'add onion , carrot , pepper and garlic', 'saute about 5 minutes or until onion is translucent', 'reduce heat to low', 'add cabbage', 'cover skillet', 'cook stirring occasionally , about 10 minutes or until cabbage wilts', 'add frankfurter', 'cook until heated through', 'meanwhile in measuring cup or small bowl stir broth , cornstarch and soy sauce until cornstarch dissolves', 'pour over franfurter mixture', 'cook and stir until slightly thickened']", "10", "355.8", "43.0", "20.0", "55.0", "23.0", "49.0", "4.0", "stir-fry", "lunch", "beginner-cook,easy,kid-friendly", "None", "None", "None", "beef,vegetables", "None", "11.97");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("335266", "the simplest bean burgers mark bittman", "this is based on a recipe from mark bittman's cookbook, 'how to cook everything vegetarian: simple meatless recipes for great food'. you can choose whatever beans you prefer. when made with chickpeas, they're golden brown and lovely; with black beans, much darker; with red, somewhere in between. lentils give you a slightly grainy texture. if you start with beans you've cooked yourself-especially well-seasoned ones-the results will be even better. like almost all veggie burger mixtures, these will hold together a little better if you refrigerate them first (ideally you'd refrigerate both before and after shaping, but that's only if you have the time). if you use the soy flakes rather than rolled oats, it will be higher in protein..", "25", "9", "['combine the beans , onion , oats , spices , salt , pepper , and egg in a food processor and pulse until chunky but not pured , adding a little liquid if necessary to produce a moist but not wet mixture', 'let the mixture rest for a few minutes if time allows', 'with wet hands , shape into whatever size patties you want and again let rest for a few minutes if time allows', 'film the bottom of a large nonstick or well-seasoned cast-iron skillet with oil and turn the heat to medium', 'a minute later , add the patties', 'cook until nicely browned on one side , about 5 minutes', 'turn carefully and cook on the other side until firm and browned', 'serve on buns with the usual burger fixings', 'or cool and refrigerate or freeze for later use']", "9", "31.0", "1.0", "5.0", "0.0", "3.0", "2.0", "1.0", "None", "lunch", "3-steps-or-less,beginner-cook,easy,kid-friendly", "beans,sandwiches", "stove-top", "None", "beans,eggs", "None", "10.11");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("221939", "kimchi fried rice kimchi bok eum bap", "the name says it all. a great way to use up left over rice. don't feel limited to the listed ingredients; mushrooms, a bit of meat, a scrambled egg etc... are all great in this dish. this recipe makes one really generous serving or two side dish servings. multiplies easily.", "15", "8", "['heat oil in wok or good-sized frying pan', 'add kim chi and garlic', 'stir-fry until kim chi is translucent', 'be careful: it will really spatter when it hits the oil !', 'add rice , green onions , fish cake or meat , mushrooms', 'stir-fry until well mixed and heated through', 'remove from heat', 'add in sesame seeds , soy sauce , salt and pepper to taste']", "9", "1217.6", "87.0", "3.0", "14.0", "29.0", "37.0", "52.0", "None", "lunch", "beginner-cook,easy", "None", "None", "None", "rice", "None", "22.94");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("126427", "crab soup dumplings dim sum", "not soup with dumplings in it, but dumplings with soup in them. this is a famous kind of steamed dumpling for dim sum. you bite a little hole in the dumpling, then carefully sip the scalding hot broth out of it, then dip the rest of the dumpling in a dip and eat. it's amazing how they get it inside-out like that. i have just learned, from a cookbook called 'dim sum and other chinese street food' by mai leung, that the secret is to gel some broth with agar-agar and seal the solid broth inside the dumplings. when the dumplings are heated, the broth liquifies again, so you get dumplings full of soup. i haven't tried this recipe yet, but i don't usually make my own dim sum, so i thought i'd go ahead and post it for those who do. she says you can only make these a few hours in advance, and if you do, you should refrigerate them before steaming.", "55", "30", "['hours or a day before making the dumplings , cook the agar-agar in the chicken broth until completely dissolved', 'pour into a shallow dish', 'cover and refrigerate for a few hours until it gels', 'mix all the filling ingredients', 'dice the gelled agar-agar / broth into small pieces and mix with the filling ingredients', 'refrigerate this mixture', 'to make the wrappers:', 'mix flour and egg', 'add water a little at a time until a soft but not sticky dough is formed', 'knead until dough is smooth and elastic', 'roll the dough into a long sausage , 1 inch in diameter', 'cut the dough into walnut-sized pices , then roll them into round balls', 'cover the balls with a damp towel', 'you are now going to make crescent-shaped dumplings', 'dust your work surface with a little flour', 'roll a piece of dough into a paper-thin circle', 'put about a tablespoon of filling in the center of the circle', 'fold the circle in half to look like a half-moon', 'pleat the top curved edge', 'pinch the pleated edge and the unpleated edge together to seal', 'gently tap the crescent's round bottom so that it can stand up', 'place the crescent-shaped dumpling between towels', 'make the remaining crescents in the same way', 'place a layer of cheesecloth in the bottom of a steamer', 'arrange the crescent dumplings on the cloth', 'cover and steam over high heat for 10 minutes', 'serve hot with ginger-vinegar-soy dip', 'to make the dip:', 'mix all dip ingredients in a serving bowl , cover , and allow to stand at room temperature for at least 10 minutes before serving', 'it should keep for hours at room temperature']", "17", "55.4", "1.0", "1.0", "12.0", "7.0", "1.0", "2.0", "None", "lunch", "None", "None", "stove-top", "None", "crab", "None", "20.37");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("394686", "ramen delight", "take your 20cent ramen package lunch to a whole new level! you'll never scoff at that package again.", "7", "12", "['1', 'add 1', '5c water and spinach to a small pot and bring it to a boil', 'use the seasoning packet to your liking in this step', '2', 'add the dry noodles to the pot to cook for 3 minutes total', '3', '2 minutes into the cooking time , slowly incorporate the beaten egg', '4', 'after the egg has cooked through , take the pot off the burner', 'serve immediately and garnish with the furikake seasoning', 'if you are not going to eat ramen right away , separate the broth from the 'solid' parts and combine before you serve it so the noodles don\'t turn to mush']", "4", "453.6", "28.0", "7.0", "76.0", "33.0", "40.0", "18.0", "None", "lunch", "5-ingredients-or-less,beginner-cook,easy", "None", "None", "None", "None", "None", "12.8");
