INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("242261", "low fat stuffed pasta shells", "supper low fat and very cheap to make! and don't for get full of flavor!", "35", "8", "['preheat oven to 350 degrees', 'bring water to a boil and add pasta shells', 'in a bowl add spinach and zucchini and wilt by heating in the microwave for 1 minute', 'then add to the cottage cheese and parmasan mixture', 'in a glass baking pan put 1 / 2 cup of marnaria sauce', 'drain and stuff shells and put in baking dish', 'cover with rest of the sauce', 'bake for 20 minutes']", "10", "69.6", "2.0", "21.0", "12.0", "11.0", "2.0", "2.0", "None", "lunch", "easy", "casseroles", "oven,stove-top", "None", "vegetables", "None", "23.42");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("16928", "papadams", "found this recipe on the net in response to a request. here's what the recipe said: ", "55", "36", "['stir together the flour , pepper , ground cumin seed , and salt in a large bowl or in the food processor', 'mix thoroughly until the spices are evenly distributed', 'add the garlic and mix well', 'add enough of the water to form a dough that will hold together in a cohesive ball', 'if necessary , add up to 1 additional tablespoon water', 'the dough should be fairly stiff and dry', 'if it is too wet , it will not roll well', 'knead the dough for about 5 minutes by hand or pulse for about 15 seconds in the food processor', 'the dough should be smooth', 'with your hands , shape the dough into a cylinder or log about 2 inches thick and 6 inches long', 'with a sharp knife , cut the cylinder into 1 / 2-inch thick slices', 'the next step uses oil , so do not use your pastry cloth and rolling pin sleeve', 'place each slice on a lightly oiled surface', 'lightly oil the top of the slice and then roll out into a very thin circle about 6 or 7 inches in diameter', 'papadams must be rolled extremely thin , maximum 1 / 16 inch', 'if the dough sticks to your rolling pin , gently pull it off', 'dust the tops of each papadam with cayenne pepper', 'using a metal spatula to loosen the edges of the papadams from the rolling surface , carefully place them on large baking sheets', 'the traditional method for preparing and frying papadams is described below', 'you can also bake them in a 300f oven for about 15 to 25 minutes , or until crisp and dry', 'the cooking time may vary beyond this range depending upon the thickness and moisture in the rolled dough', 'cool them on a rack', 'to prepare the papadams in an authentic manner , don't bake them', 'first allow them to dry by leaving them in the sun for a few hours or by placing them in a very low oven for 1 to 2 hours , turning occasionally', 'dry the papadams until they are just slightly flexible and show no moisture', 'they should not brown or bake , however', 'at this point , the papadams may be stacked flat and stored in an airtight container until needed', 'they will keep for months if stored properly', 'when ready to serve , heat about 1 / 8 inch of oil in a large skillet', 'the oil should be hot , but not smoking', 'a drop of water flicked into the skillet should sizzle immediately', 'one at a time , drop the papadams into the hot oil', 'turn the papadam when it begins to curl at the edges', 'it will quickly fry and become crisp', 'remove it before it turns brown', 'cool and drain the papadams on paper towels and eat them immediately']", "8", "60.8", "1.0", "6.0", "4.0", "7.0", "0.0", "3.0", "deep-fry", "lunch", "kid-friendly", "beans,rolls-biscuits", "food-processor-blender,oven,stove-top", "dinner-party,picnic", "beans", "None", "19.88");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("109483", "lo bak go chinese radish cakes", "this is my favourite chinese dim-sum. it is a steamed cake of rice flour and grated chinese radish (lo-bak), which is then cut into slices and pan fried. not unlike scrapple, in some ways, although less meaty. it's delicious with lots of hot sauce. lo-bak looks very similar to daikon; it is just a little bit milder in flavour. i suspect daikon would work okay if need be. you don't have to use both the shrimp and the shiitake; one or the other would be alright.", "180", "13", "['soak the shrimp and mushrooms in the boiling water', 'peel and grate the radish directly into the chicken stock', 'simmer for about 15 minutes', 'drain the chicken stock from the lo bok , and the soaking water from the shrimp and mushrooms , and reserve them both', 'mince the shrimp and the shiitake', 'add the combined stock and soaking water slowly to the rice flour , stirring constantly , to make a batter of medium viscosity', 'season with salt , if required , and a little pepper', 'mix the lo bak , shrimp and shiitake into it , and put it in a large sauce pan', 'cook , stirring constantly , until the mixture begins to thicken', 'pour it into an oiled 10' square cake pan , and cover it tightly in foil', 'bake it at 300 deg in a water bath until thoroughly set- 1 1 / 2 to 2 hours ?', 'let cool', 'slice into 1 / 2' slices and fry until nicely browned on both sides in a lighlty oiled pan']", "7", "58.7", "0.0", "2.0", "12.0", "3.0", "0.0", "3.0", "None", "lunch", "None", "None", "None", "None", "rice,vegetables", "winter", "16.0");
