INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("511116", "pork and green bean stir fry", "a picture tells a thousand words and the photograph of this recipe in southern living magazine looks wonderful. feel free to sub strips of red bell peppers in place of the hot chiles. **the key to this dish is developing a nice char on the green beans. to do so, heat the oil in your skillet until it almost smokes before adding the beans. let them sear, stirring every 20 - 30 seconds, and they'll take on color.", "30", "8", "['brown ground pork and next 3 ingredients in a large stainless steel skillet over medium high heat , stirring often , 7 - 8 minutes or until meat crumbles and is no longer pink', 'drain', 'wipe skillet clean', 'cook green beans in hot oil in skillet over medium high heat , stirring occasionally , 4 -5 minutes or until just tender and slightly charred', 'stir together teriyake sauce and next 2 ingredients', 'stir pork mixture into green beans', 'stir in teriyaki mixture , tossing to coat', 'serve immediately over rice or noodles']", "12", "547.1", "61.0", "38.0", "102.0", "66.0", "70.0", "4.0", "None", "lunch", "easy,kid-friendly", "pork", "None", "dinner-party", "pork,vegetables", "None", "18.57");
