INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("49945", "the best coffee lover s coffee cake", "this is the cake i've reserved all through 2002, just to welcome 2003! my guests are in for a treat and can you imagine, i've waited the whole year long to post this:) try it! it lives up to its name, imo.", "70", "18", "['combine flour , coffee granules , salt , baking soda , cinnamon and brown sugar in a large sized bowl', 'mix well', 'keep aside', 'in another bowl , beat an egg until light and frothy', 'add butter and mix well', 'add the dry ingredient mixture alternating with the sour cream', 'mix well', 'add nuts', 'mix', 'pour into a greased 9 inch cake tin', 'bake at 180c for 40 minutes or till a wooden toothpick inserted in the center comes out clean', 'allow to cool completely on wire racks', 'slice and serve for a wonderful breakfast dish to go with your morning or afternoon coffee', 'enjoy while you sing- ring out the old , ring in the new , ring happy bells across the snow !', 'ho !', 'ho !', 'ho !', 'happy new year !']", "10", "4556.5", "318.0", "1708.0", "143.0", "109.0", "484.0", "215.0", "None", "breakfast", "None", "None", "oven", "None", "fruit", "None", "35.91");
