INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("218605", "tex mex chicken sandwiches", "i make these often in the summer months for a light meal as an alternative to hamburgers. serve with grilled corn on the cob. they are fantastic!", "72", "6", "['place chicken breasts in plastic zippered bag with salsa or picante sauce', 'zip shut and refrigerate 1 hour', 'place on barbecue grill and cook for 5 minutes on each side', 'top with cheese and close top of grill until cheese melts', 'spread bottoms of each hamburger bun with avocado mixture', 'top with lettuce , tomato , chicken breasts and top of buns']", "8", "470.7", "33.0", "23.0", "37.0", "76.0", "40.0", "10.0", "broil,grilling", "lunch", "3-steps-or-less,beginner-cook,easy", "sandwiches", "oven", "camping", "chicken", "summer", "35.89");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("59446", "nigella lawson caramelized onion lentil pilaf", "came across this recipe on nytimes.com in an article written by the kitchen goddess herself: nigella lawson. haven't tried it yet, but i thought i'd share this. i'm drooling over the thought of it! :) i did omit the ghee to make it healthier.", "40", "13", "['in a large heavy-based saucepan , heat over medium heat', 'add onion slices , and saut until softened and deep golden brown', 'remove half the onions and set aside', 'reduce heat to medium-low and add turmeric , cumin , coriander , cloves , mustard seeds and curry leaves', 'add salt , and season with black pepper to taste', 'combine lentils and rice in a fine-meshed sieve , and rinse well with cold water', 'drain , then add to pan', 'add 4 cups water , raise heat , and bring to a boil', 'cover , reduce heat to very low , and cook for 15 to 20 minutes', 'water should be completely absorbed', 'if not , remove pan from heat , remove lid and cover pan with a kitchen towel , then replace lid and allow to stand for 10 minutes', 'to serve , fluff pilaf with a fork , and transfer to a serving bowl', 'garnish with reserved onions']", "11", "238.9", "2.0", "6.0", "32.0", "22.0", "1.0", "15.0", "None", "lunch", "beginner-cook,easy", "beans", "stove-top", "dinner-party", "beans,rice,vegetables", "None", "31.88");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("131214", "creamy lentil risotto kichdi", "this is my version of my mom's recipe that was made for us so often while growing up. this is sort of the indian cousin of the risotto, since the rice grains are not separate but cooked to a porridge-like consistency. it is immensely flavorful even in the simple version i have written, but you can make a heartier version by adding extra veggies to it too. my mom used to add carrots, potatoes, and peas to it sometimes. it's delicious with pickles or a simple vegetable curry, but can be eaten by itself with a dab of butter to make it even more creamy. you'll find there is nothing too distinctive about the ingredients in this recipe, but the technique of cooking is what makes this dish decidedly different from regular pilafs etc.", "50", "12", "['rinse the rice and lentils in several changes of water , and let soak for 1-4 hours', 'drain', 'add enough water to cover the lentils and rice by 1 inch , and bring to a boil', 'reduce heat and add turmeric and salt', 'cover and let simmer on low heat until rice and lentils are tender , about 30mins', 'keep checking to see if water needs replenishing , you don't want it to dry out', 'stir often so it doesn't stick at the bottom', 'keep adding water till rice is overcooked and you have a thick porridge-like consistency', 'in a small saucepan , heat ghee and then add the cinnamon stick , bay leaves , cloves , cardamom and cumin seeds', 'when they are fragrant and splutter , add the onion and garlic and cook till onion is softened', 'pour this mixture into the lentil-rice mixture and stir to combine well', 'just before serving serving , drizzle with lemon juice if desired']", "14", "145.3", "9.0", "4.0", "0.0", "7.0", "16.0", "6.0", "None", "lunch", "None", "beans", "stove-top", "None", "beans,rice,vegetables", "None", "29.37");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("14339", "bulgogi korean barbecued beef lettuce wraps", "this dish is great for hot summer days. the crisp lettuce makes a nice wrapper, but doesn't leave you overstuffed like a starchy wrapper can", "35", "11", "['heat a nonstick pan at medium-low for about 30 seconds', 'add sesame seeds and toast , shaking or stirring pan occasionally , until seeds are a nice golden brown , 2 to 3 minutes', 'set aside', 'combine soy sauce , sugar , garlic , green onions , ginger , sesame oil , and toasted sesame seeds', 'stir until the sugar has more or less dissolved', 'add meat , tossing to coat', 'marinate 30 minutes or up to overnight', 'grill meat on a rack over hot coals until beef firms up and slightly charred', 'you want that nice glossy brown look , not the sweaty grayish look', 'serve with lettuce leaves and asian pear slices , allowing guests to roll wraps themselves', 'to assemble , layer a lettuce leaf with a slice or two of grilled beef and a few thin asian pear slices , then roll up and enjoy !']", "10", "109.0", "8.0", "40.0", "47.0", "6.0", "3.0", "4.0", "None", "lunch", "easy,kid-friendly", "None", "stove-top", "picnic", "beef,fruit,vegetables", "None", "22.75");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("274972", "green onion cakes", "these chewy fried chinese flatbreads are delicious! they are common at fairs and festivals in edmonton, alberta, canada where i lived for 5 years. they are commonly served with fiery hot chinese chili garlic sauce that you can find at most grocery stores. if you're not into spicy food, you can serve this with plum sauce or sweet and sour dipping sauce instead. although, these are lengthy instructions, the green onion cakes are pretty easy to make and well worth the effort. if you don't want to cook them all at once, you can freeze the uncooked cakes, then thaw and fry them as needed. if you freeze them, separate each cake with a small piece of wax paper.", "135", "17", "['add salt to flour in a mixing bowl and mix throughly', 'add honey to hot water in a separate cup or dish until the honey melts and mixes with the water', 'slowly add water / honey mixture into the flour while stirring the flour', 'knead the mixture until smooth', 'if it is too dry add a little extra water', 'if it is too wet , add a little flour', 'cover with plastic wrap and refrigerate for at least 1 hour', 'after at least an hour remove mixture and roll into a sausage shape and divide into 10 equal portions', 'you will work with one cake at a time so cover the others so they don't dry up', 'for each portion of dough , roll into a ball between the palms of your hands , then on a floured surface roll the dough out into a thin round pancake', 'sprinkle the surface of the dough with green onions', 'roll the pancake into a sausage shape , trapping all the green onions inside the dough', 'roll the dough again along the length of the sausage to make a coil shape', 'with the coil still standing up on your counter top flatten it with the palm of your hand', 'roll the dough into a circle about 5 inches in diameter and place to the side while you make the rest of the cakes', 'heat up the oil in a frying pan or skillet at a medium heat and fry each cake , one side at a time , until it is light brown', 'cut into quarters and serve with chili garlic dipping sauce']", "6", "120.3", "4.0", "3.0", "4.0", "5.0", "1.0", "6.0", "None", "lunch", "None", "None", "None", "None", "None", "None", "38.76");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("389844", "super simple gyros with cucumber mint yogurt", "looking for something that you can pull together for lunch or dinner in a jiffy? this easy, greek-inspired gyros recipe may be the way to go. to serve: heat and split the pitas; serve the lamb, seasoned tomatoes and feta, cucumber-mint yogurt, onion, and lettuce in separate dishes. have your family and friends stuff the warm bread pockets to their liking. enjoy!", "25", "14", "['mix yogurt , mint , sugar and salt in a small bowl', 'stir in the cucumber', 'cover and chill for at least 30 minutes', 'season the diced tomato , feta cheese , and parsley with the red wine vinegar , olive oil , salt and pepper', 'set aside', 'in a large non-stick skillet , heat the oil over medium heat until hot', 'add the ground lamb , oregano , thyme , garlic and onion powders , and black pepper', 'cook until browned - stirring to crumble', 'drain on paper towel-lined plate', 'transfer lamb to to a mixing bowl', 'add the lemon juice and stir well', 'warm the pita bread rounds', 'split open each pita half forming a pocket', 'have everyone spoon even amounts of the lamb mixture into their pita pocket , and top with onions , lettuce , tomatoes and feta , and cucumber-mint yogurt']", "19", "530.9", "52.0", "31.0", "22.0", "54.0", "78.0", "9.0", "None", "lunch", "kid-friendly,toddler-friendly", "sandwiches", "refrigerator,stove-top", "None", "lamb-sheep", "None", "25.78");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("12669", "gold rice with chicken and pistachio", "wonderful rice and chicken, pistachios add a nice touch.garnish plate with crisp romaine lettuce, topped with chopped tomatoes and black olives, sprinkled with salt and pepper and splashed with a little balsamic vinegar.", "40", "6", "['heat oil in a large no stick frypan over medium high heat until hot', 'add chicken strips and onion , cook 5 minutes or until chicken is browned and onion is tender , stirring often', 'add all remaining ingredients except nuts', 'mix well', 'bring to boil and reduce heat to medium low , cover and cook 15-20 minutes or until chicken is no longer pink , rice is tender and liquid is absorbed', 'just before serving , sprinkle nuts over top']", "12", "436.7", "15.0", "31.0", "38.0", "68.0", "9.0", "16.0", "None", "lunch", "easy,one-dish-meal", "None", "stove-top", "dinner-party", "chicken,fruit,grains,rice,white-rice", "None", "16.2");
