INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("125845", "japanese tuna spring onion topping for rice", "i found this recipe on the internet. i like canned tuna.this is inexpensive and simple to make! this recipe is great when you are lazy to cook but want something yummy.", "5", "2", "['combine all the ingridients in a boul', 'serve on the top of warm rice']", "4", "50.6", "3.0", "0.0", "31.0", "13.0", "2.0", "0.0", "None", "lunch", "3-steps-or-less,5-ingredients-or-less,beginner-cook,easy", "None", "None", "None", "rice,white-rice", "None", "11.31");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("4356", "italian sausage sweet", "to stuff the casings, you will need a meat grinder with a sausage horn attachment. if you don't have such a device, use the mixture to make fried patties by shaping 1/4 cup of filling into a 1/2 inch thick patty and frying in hot oil until golden brown on both sides.", "45", "11", "['to prepare casings: let casings soak in cool water about five minutes to remove salt on outer surface and flush salt from the inside by placing one end on faucet nozzle and turn on cold tap water', 'remove casing from faucet and gently squeeze out water', 'cover rinsed casings and refrigerate until ready to use', 'mix all ingredients very well in a large bowl', 'fill the casings with the mixture and make links by twisting the sausage where you wish the links to be', 'four inches is a good size for a regular serving , smaller links may be made for appetizer servings', 'place the sausage in large saucepan or dutch oven in a single layer', 'cover and heat over high heat to a low simmer', 'reduce heat to maintain low simmer until the sausage is heated through , approximately 15 minutes', 'drain and let rest for about 15 minutes before slicing', 'serve while warm']", "8", "1057.5", "109.0", "1.0", "69.0", "171.0", "123.0", "1.0", "None", "breakfast", "easy", "pork", "None", "None", "pork", "None", "32.73");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("30081", "ground beef gyros", "this is a great summer meal and quick and easy to put together and tastes wonderful. refreshing served with the cucumber sauce and is even low in fat. i think this came from quick cooking. it's a favorite in our house.", "22", "10", "['in a bowl , combine first 5 ingredients', 'cover and refrigerate', 'in separate bowl , combine ground beef and seasonings', 'mix well', 'shape into 4 patties', 'grill , covered , over medium to high heat for 10 to 12 minutes or until meat is no longer pink , turning once', 'cut patties into thin slices', 'stuff into pitas', 'add lettuce , cucumber , tomato , and onion', 'serve with yogurt sauce']", "15", "436.4", "21.0", "29.0", "18.0", "63.0", "30.0", "14.0", "broil,grilling", "lunch", "easy", "sandwiches", "oven", "None", "beef", "spring,summer", "11.23");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("81105", "marinated stuffed cherry peppers", "i originally found this recipe here on recipezaar from cc. http://www.recipezaar.com/recipe/getrecipe.zsp?id=34555 after playing around with it i came up with this variation. hope you enjoy!", "20160", "20", "['wash cherry peppers and place whole , stems intact , in 1 gallon jar', 'fill jar with white vinegar', 'let stand at least one week', 'i've let them soak as long as 3 weeks', 'you may need to add more vinegar to keep the jar full as the peppers soak it in', 'the peppers will float , make sure to keep them covered', 'after soaking for at least a week , pour out vinegar and rinse peppers in cool water', 'remove stems and seeds', 'expect to lose 3 or 4 peppers during soaking', 'cut provolone to the appropriate size to fill the peppers', 'cut the proscuitto slices in half', 'wrap the provolone with a 1 / 2 slice of proscuitto and stuff into pepper', 'add some olive oil to the jar', 'add onion , rosemary , 2 or 3 bay leaves , dill weed , basil and chopped garlic to oil', 'place the peppers in the jar and fill the jar with 50% olive oil and 50% canola oil', 'top off the jar with a sprikle coating of parmesan cheese', 'let soak a minimum of one week', 'i let them soak and store them at room temperature', 'you may want to refrigerate for longer shelf life', 'enjoy !']", "13", "376.3", "61.0", "5.0", "2.0", "3.0", "24.0", "0.0", "None", "lunch", "None", "None", "refrigerator", "None", "vegetables", "fall", "28.64");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("25875", "mighty good moussaka", "there are many moussaka recipes but i have found none as good as this recipe. originally it was from 'the complete garlic lovers cookbook from gilroy, the garlic capital of the world' i made a few minor changes and this recipe is the result. i've never had anyone turn down seconds and have served it at least a dozen times.", "110", "13", "['peel the zucchini and slice into 1 / 2-inch rounds', 'heat a skillet and spray with the veggie oil', 'quickly brown the zucchini slices on both sides , spray with oil as necessary arrange half the zucchini slices in an oiled 9x12x2-inch baking dish', 'heat the olive oil in the same skillet and saut the onion and garlic until translucent', 'add meat , stir to break up any lumps , and cook for about 5 minutes', 'in a saucepan heat the tomato sauce , tomato pieces , bay leaf , beau monde , honey , oregano , chili pepper and black pepper', 'simmer gently for 10 minutes', 'meanwhile , in a separate pan , heat a little oil and brown the mushrooms , then add them to the meat and mix well', 'pour all of the meat mixture over the zucchini slices', 'top with cinnamon , allspice and cottage cheese', 'cover with remaining zucchini slices , then the tomato sauce and wine', 'sprinkle with the cheese and bake for 1 hour in a 350f oven', 'remove from oven , sprinkle with the parsley , serve']", "21", "425.4", "37.0", "55.0", "19.0", "51.0", "31.0", "6.0", "None", "lunch", "easy", "casseroles", "oven", "dinner-party", "beef,cheese,vegetables", "None", "22.39");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("423521", "lomo saltado vegan", "lomo saltado is a peruvian dish with chinese influences. this dish is normally prepared with beef tenderloin, potatoes, and vegetables and served with rice - but this version ditches the beef!! the recipe calls for wheat gluten strips but you could also use seitan, tofu, or portabella mushrooms. look for jarred aji yellow paste or aji amarillo paste in a latin food market. if you can't find the paste, you can use frozen aji amarillo peppers (south american hot yellow chile pepper) or equivilant pepper. thaw and dice one pepper, and saute it in olive oil until it's soft. this recipe is from vegan international.", "35", "11", "['prepare the rice and set aside', 'heat oil to 325 , cut potatoes into french fries and fry for 5 minutes', 'remove from oil', 'reheat oil to 375 and resume cooking fries for about 2-3 more minutes until golden brown , crispy , and cooked through', 'keep warm', 'heat 2 tablespoons oil in pan over medium-high heat', 'add onion , gluten strips or mushrooms and fry until browned', 'add aji , tomatoes , parsley , cilantro , vinegar , soy sauce , and spices and cook for 2 minutes until tomatoes are softened', 'if desired , sprinkle pisco on top of the dish and ignite', 'squeeze lime juice over pan , add french fries , and quickly toss together', 'serve over rice']", "18", "980.3", "15.0", "26.0", "29.0", "192.0", "5.0", "42.0", "None", "lunch", "one-dish-meal", "potatoes", "stove-top", "dinner-party", "potatoes,vegetables", "None", "12.28");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("204481", "broccomoli", "this comes from the taste of home website...i was skeptical at first, but as a calorie watcher and a broccoli lover i gave it a try. the original recipe did not call for garlic, but i found that garlic was delicious! i suppose you could experiment with cilantro or cumin as well. don't taste it until it is well chilled or you will be disappointed. try not to use the preminced bottled garlic...throw in a real clove (or less)...it is fantastic...but very strong garlic flavor with one clove!", "185", "5", "['place all ingredients in a food processor and blend until very smooth', 'chill at least a few hours before tasting', 'can serve with chips , crackers , or as a veggie dip', 'keeps well in fridge', 'cooking time listed is for chilling time']", "7", "10.4", "0.0", "3.0", "0.0", "1.0", "0.0", "0.0", "None", "lunch", "beginner-cook,easy", "None", "food-processor-blender", "None", "vegetables", "None", "19.53");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("311039", "chicken florentine crepe ihop s copycat", "i just love this dish! i never knew how yummy spinach can be till i tried this entree in ihop. it was a limited time only dish and when they took it out, i searched through the internet for the recipe but couldn't find one so i decided to take the matters into my own hands! this recipe is really easy and the ingredients may already be in your fridge! serve it alone or with garlic bread or steamed veggies on the side. notes: you can substitute chicken with leftover turkey or chicken rotisserie, lets face it, nobody ever eats those leftover turkey and rotisserie! you can also serve this as an appetizer. just cook smaller proportions of crepes. and for those on protein diet, substitute the crepe with omelet!", "40", "21", "['combine flour , milk , eggs , butter and salt in a bowl', 'beat until all the flour lumps are gone', 'the consistency will be like thin pancake batter', 'set aside', 'saut chicken and garlic on butter until golden brown', 'pour in milk and cream of chicken', 'simmer', 'reduce to low to medium heat', 'after simmer , put the spinach and cheese inches keep stirring until the cheese completely melts', 'salt and pepper to taste', 'make sure the bottom wont stick', 'keep simmering until the sauce is reduced', 'while the chicken is simmering , start cooking the crepes', 'in a lightly greased skillet , pour about 3 tbsp of batter and spread it as thin as possible', 'do not flip', 'in about 1 minute , check the bottom side of the crepe if light brown', 'cook only one side of the crepe or else it will be crunchy', 'by the time you're done with the crepes , chicken should be done too', 'pour about 3 / 4 cup of chicken on each crepe on the center', 'wrap each side', 'top lightly with parmesan cheese']", "12", "475.5", "42.0", "3.0", "35.0", "62.0", "64.0", "8.0", "None", "breakfast", "beginner-cook,easy,kid-friendly", "pancakes-and-waffles", "stove-top", "dinner-party", "cheese,chicken,vegetables", "None", "13.96");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("71867", "hyderabadi khatti dal mango lentil curry from the indian state", "a wonderful recipe submitted by syeda basarath to this week's thursday magazine. totally delicious and a must have for mango lovers!", "60", "16", "['put the first three ingredients in a pressure cooker', 'pressure cook for upto 2-3 whistles', 'remove from flame and let out the steam', 'mix the cooked lentils with water using a wooden spoon', 'boil the mixture for 5 minutes', 'heat oil in a pan', 'add cumin seeds and mustard seeds', 'allow to splutter', 'once they stop spluttering , add curry leaves', 'stir-fry until fragrant', 'add garlic and stir-fry until the raw smell is gone', 'mix in the red chilli and turmeric powders and the corriander leaves', 'fry till it turns slightly brown', 'pour over the boiled lentils', 'serve hot over cooked white rice', 'enjoy a hearty lunch !']", "13", "586.0", "25.0", "118.0", "2.0", "52.0", "10.0", "30.0", "stir-fry", "lunch", "None", "beans", "pressure-cooker,stove-top", "dinner-party", "beans,fruit,vegetables", "summer", "34.12");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("138357", "bunuelos", "i love ordering these, soo yummy! they are easy and a good quick dessert. from texascooking.com.", "20", "8", "['combine sugar , cinnamon and nutmeg in a large plastic bag , mixing well', 'set aside', 'cut tortillas into 3x2 inch strips , or just into strips', 'fry a few at a time in 1 inch of hot oil until crisp and golden , turning once', 'drain on paper towels', 'while still warm , place a few at a time in sugar mixture in bag', 'shake gently to coat', 'store in airtight container']", "5", "479.9", "13.0", "141.0", "29.0", "18.0", "10.0", "30.0", "None", "lunch", "3-steps-or-less,5-ingredients-or-less,beginner-cook,easy", "None", "None", "None", "grains", "None", "22.74");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("44170", "chinese chicken wraps", "another type of asian appy roll. get your hot mustard and sweet & sour ready, :)", "30", "12", "['preheat oven to 375 degrees f', 'beat egg lightly and set aside for egg wash', 'mix together honey , soy sauce , and dry mustard in a bowl', 'add chicken , green onion , and pineapple and stir well', 'unroll crescents and separate into 4 pieces and pinch together the seams', 'cut each triangle crosswise into halves', 'spoon 1 tablespoonful of mix onto each dough piece , pull corners to center , and roll up to seal', 'brush top of roll with more egg wash and sprinkle with seame seeds', 'place on an ungreased baking sheet while waiting for others to get done', 'repeat procedure with rest of dough and filling', 'bake in preheated oven for 10-15 minutes , or until rolls are a nice golden brown', 'serve with hot mustard or sweet and sour sauce , if desired']", "9", "173.4", "7.0", "24.0", "14.0", "18.0", "5.0", "7.0", "None", "lunch", "None", "None", "oven", "None", "chicken,eggs,fruit,grains,pineapple", "None", "24.83");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("408764", "toasted turkey subs with smoked mozzarella", "published in cook's country magazine, dec/jan 2007. a nice italian dinner sandwich.", "16", "9", "['adjust oven rack to upper-middle position and heat oven to 500f', 'heat oil , garlic , and red pepper flakes in a large skillet over medium-high heat until fragrant , about 1 minute', 'add tomatoes and cook until mixture is dry , about 10 minutes', 'turn heat off', 'stir in 2 tablespoons pesto and salt and pepper to taste', 'spread remaining pesto on insides of each roll and layer each roll with turkey , tomato mixture , and sliced cheese', 'place sandwiches on baking sheet , with open sides facing up', 'bake until edges of bread are golden and cheese begins to melt , about 5 minutes', 'serve']", "10", "912.7", "60.0", "69.0", "148.0", "110.0", "67.0", "29.0", "None", "lunch", "3-steps-or-less,easy", "sandwiches", "None", "None", "turkey,vegetables", "None", "12.33");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("451785", "sarasota s green chili mexican burger", "these are easy but very full of flavor. i also prefer to use turkey for this, but if you are a die hard 'beef fan' go ahead and use beef. the burger is moist and delicious and the cheese, avocado and mayonnaise just make this a wonderful burger.", "25", "30", "['mayonnaise -- in a small bowl , add the green chilies mayonnaise , garlic and lime juice and mix well', 'if you want , you can blend this in a small food processor or blender , but i actually like a little texture to mine', 'cover and refrigerate until ready to use', 'this can easily be made ahead of time', 'burgers -- in a medium size bowl , add the salsa , chili powder , cumin , grated onion , pinch of salt and pepper', 'if you use a mild salsa you can add a bit more chili powder , but i use a medium heat salsa which works just fine for this', 'mix everything well', 'then add in the turkey and mix lightly , i prefer to use my hands', 'and make sure not to over work the burgers', 'make 4 nice size burgers and place on a small sheet pan lined with either foil or parchment', 'make a small indentation in the center of the burger with your thumb', 'not too deep , just a little indent', 'this helps the burger not to shrink up as it grills', 'season with salt and pepper and put in the freezer', 'we are not freezing them , just firming them up', 'they cook better after they rest a bit and are chilled', 'about 15-20 minutes in the freezer', 'these also can be made a head of time', 'garnish -- prepare your avocado slices and drizzle with the remaining lime juice , lettuce leaves and cheese', 'grill -- i prefer an outside grill , but a inside grill pan or even a heavy saute pan will work just fine', 'spray the pan or grill with a non stick spray and cook the burgers on the first side until you get a nice sear and golden brown , depending on how thick you make them , about 5 minutes', 'flip , and make sure when you flip , the burger releases from the pan or grill easily', 'if you have to scrape to flip it , it isn't ready to be flipped', 'the second side again will take about 5 minutes , maybe a bit less', 'add 2 slices of cheese per burger and melt', 'as the cheese melts , add the burger rolls either to a broiler or grill to lightly brown', 'finish -- top the bottom bun with a thin slather of the mayonnaise , then a lettuce leaf', 'then the burger with melted cheese , another lettuce leaf , avocado slices and top with the second bun slathered with the mayonnaise', 'serve -- enjoy ! a crisp green salad goes great with this', 'fries of course or chips and a pickle']", "15", "834.6", "81.0", "34.0", "42.0", "101.0", "87.0", "14.0", "None", "lunch", "easy", "burgers", "None", "dinner-party", "turkey", "None", "11.45");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("129744", "ceviche tacos", "cerviche is a mexican appetizer, kind of like shrimp cocktail but better. this is served in almost all mexican restaurants. try it and let me know.....", "25", "13", "['finely grate rind from limes to measure 1 tablespoon', 'juice limes to measure 1 / 4 cup', 'place rind and juice in a large bowl', 'add tomato , avocado , cilantro , salt , pepper , garlic and shrimp', 'if you want , you can add some chopped onion and fresh jalapenos', 'toss well to combine', 'cover and chill for 15 minutes , stirring occasionally', 'heat tortillas', 'spoon about 1 / 2 cup of shrimp mixture down the center of tortilla', 'fold and eat', 'you can serve it plain and / or eat with crackers or you can use hard tacos shells , tostadas , tortilla chips or whatever you like', 'my dad eats it with saltine crackers', 'he adds extra onion and jalapenos , personal preference !']", "9", "138.6", "7.0", "6.0", "16.0", "33.0", "4.0", "2.0", "None", "lunch", "None", "shrimp", "None", "None", "shrimp", "None", "19.15");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("12732", "rice verde for one or two", "very good rice, serve with mexican dishes. recipe can be cut in half to make for one.", "25", "5", "['place half of the rice in two buttered ramekins', 'combine sour cream , chilies and half the cheese in a mixing bowl', 'mix and spread over rice and cover with remaining rice', 'top with the other half of the cheese', 'bake in a 350f oven for 15 minutes']", "4", "340.2", "24.0", "2.0", "7.0", "22.0", "48.0", "12.0", "None", "lunch", "beginner-cook,easy", "None", "oven", "None", "rice", "None", "28.13");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("456631", "easy mini muffulettas", "one for the new orleans way.", "31", "5", "['spread 1 tablespoon olive salad evenly over each cut side of roll bottoms', 'top each with 1 swiss cheese slice , 1 ham slice , 1 tablespoons olive salad , 1 provolone cheese slice , 1 salami slice and 1 tablespoon olive salad', 'cover with roll tops and wrap sandwiches together in a large piece of aluminum foil', 'place on a baking sheet', 'bake at 350 for 14 to 16 minutes or until cheeses are melted']", "5", "518.0", "38.0", "4.0", "90.0", "66.0", "60.0", "12.0", "None", "lunch", "3-steps-or-less,easy", "sandwiches", "None", "birthday,picnic", "cheese", "None", "39.08");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("42043", "6 step chicken tikka", "d-e-l-i-c-i-o-u-s!", "140", "6", "['cut the chicken and make deep cuts on the pieces', 'mix all the ingredients', 'marinate the chicken for a minimum of 2 hours', 'deep fry', 'drain on paper towels', 'serve']", "9", "341.4", "35.0", "6.0", "4.0", "57.0", "33.0", "0.0", "deep-fry", "lunch", "None", "None", "None", "None", "chicken", "None", "30.64");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("17507", "bread machine spinach feta bread", "found on the net in response to a request. i know nothing about bread machines, so the timing here is a total guess on my part :-)", "80", "4", "['defrost spinach but do not cook', 'squeeze out all the liquid', 'bring all ingredients to room temperature and add to machine', 'select white bread cycle']", "12", "213.0", "9.0", "9.0", "17.0", "12.0", "8.0", "11.0", "None", "lunch", "3-steps-or-less,easy", "None", "bread-machine", "dinner-party,picnic", "cheese,eggs,vegetables,yeast", "None", "12.37");
INSERT INTO recipes (id, name, description, cooking_minutes, n_steps, steps, n_ingredients, calories, total_fat_pdv, sugar_pdv, sodium_pdv, protein_pdv, saturated_fat_pdv, carbohydrate_pdv, cooking_method, course, difficulty, dish, equipment, event, key_ingredient, season, cost) VALUES ("181091", "steamed chicken zheng ji with ginger scallion dipping sauce", "chicken rubbed with salt and then steamed, served with a ginger-scallion dipping sauce to bring out its full flavor. serve with steamed rice. serves 4 as a main course or 8 as part of a multi-dish chinese style dinner.", "85", "11", "['rinse the chicken in cold , running water , and blot dry completely with paper towels', 'rub the salt all over the skin of the chicken and inside the cavity', 'place the chicken , breast-side down , on a heat proof platter , and set aside for 15 minutes', 'set up your steamer , or place a rack inside a wok or other deep pan', 'fill with about 2 inches of hot water', 'cover tightly and gently steam over medium heat for 1 hour , replenishing the water from time to time', 'remove the platter of cooked chicken and pour off all of the liquid', 'in a small bowl , combine the sugar , salt , soy sauce , ginger , garlic , and scallions', 'in a small pan , combine the oils and heat until they smoke', 'pour the hot oils over the ginger-scallion mixture', 'chop the chicken into serving sized pieces and serve with dipping sauce']", "10", "361.0", "42.0", "0.0", "56.0", "50.0", "36.0", "0.0", "None", "lunch", "easy", "None", "None", "None", "chicken,fruit,vegetables", "fall,spring,winter", "30.52");
